plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Host Server" as host {
  node "Go Application\n(ssh-key-agent)" as go_app
  artifact "Go Binary" as go_bin
}

cloud "External Systems" as ext {
  node "SSH Servers" as ssh_servers
  node "HTTP APIs" as http_apis
}

node "Environment Variables" as env_vars

go_app --> env_vars : Reads configuration

go_app --> ssh_servers : SSH

go_app --> http_apis : HTTP

go_bin -up-> go_app : Built from

@enduml
