plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

' Deployment Diagram for AI Code Generation to IaC
node "Developer Workstation" as dev {
  artifact "Source Code (Python, Terraform)" as src
  artifact "Pre-commit Hooks" as precommit
}

node "GitHub" as github {
  artifact "GitHub Actions CI/CD" as cicd
}

node "AWS Cloud" as aws {
  node "Terraform Module" as tfmod {
    artifact "AWS S3 Bucket" as s3
    artifact "AWS DynamoDB" as dynamodb
    artifact "AWS IAM Roles" as iam
    artifact "AWS Route53" as route53
    artifact "AWS EC2 Instances" as ec2
    artifact "AWS SSM Parameter Store" as ssm
  }
}

' Relationships
src -down-> precommit : "Code Quality & Security"
precommit -right-> github : "Push/PR"
github -down-> cicd : "CI/CD Pipeline"
cicd -down-> tfmod : "Deploys via Terraform"
tfmod -down-> s3 : "Creates/Manages"
tfmod -down-> dynamodb : "Creates/Manages"
tfmod -down-> iam : "Creates/Manages"
tfmod -down-> route53 : "Creates/Manages"
tfmod -down-> ec2 : "Creates/Manages"
tfmod -down-> ssm : "Creates/Manages"
@enduml
