plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "AWS ECS Cluster" as ecs {
  node "API Service\n(Go + Gorilla Mux)" as api
}

node "AWS ECR\n(Container Registry)" as ecr

database "PostgreSQL\n(AWS RDS)" as db

api --> db : Connects via lib/pq
api --> ecr : Pulls container image
api ..> ecs : Deployed on

@enduml
