terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = ">= 4.0"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDR blocks."
  type        = list(string)
  default     = ["10.0.1.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDR blocks."
  type        = list(string)
  default     = ["10.0.2.0/24"]
}

variable "mlflow_container_image" {
  description = "Container image URI for MLflow + Nginx."
  type        = string
}

variable "db_username" {
  description = "Username for the RDS PostgreSQL instance."
  type        = string
}

variable "db_password" {
  description = "Password for the RDS PostgreSQL instance."
  type        = string
  sensitive   = true
}

variable "db_allocated_storage" {
  description = "Allocated storage for RDS (in GB)."
  type        = number
  default     = 20
}

variable "db_instance_class" {
  description = "Instance class for RDS."
  type        = string
  default     = "db.t3.micro"
}

variable "mlflow_s3_bucket_name" {
  description = "Name for the S3 bucket to store MLflow artifacts."
  type        = string
}

resource "aws_vpc" "main" {
  cidr_block = var.vpc_cidr
  enable_dns_support   = true
  enable_dns_hostnames = true
  tags = {
    Name = "mlflow-vpc"
  }
}

resource "aws_internet_gateway" "main" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "mlflow-igw"
  }
}

resource "aws_subnet" "public" {
  count                   = length(var.public_subnet_cidrs)
  vpc_id                  = aws_vpc.main.id
  cidr_block              = var.public_subnet_cidrs[count.index]
  map_public_ip_on_launch = true
  availability_zone       = element(data.aws_availability_zones.available.names, count.index)
  tags = {
    Name = "mlflow-public-subnet-${count.index + 1}"
  }
}

resource "aws_subnet" "private" {
  count             = length(var.private_subnet_cidrs)
  vpc_id            = aws_vpc.main.id
  cidr_block        = var.private_subnet_cidrs[count.index]
  availability_zone = element(data.aws_availability_zones.available.names, count.index)
  tags = {
    Name = "mlflow-private-subnet-${count.index + 1}"
  }
}

resource "aws_route_table" "public" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "mlflow-public-rt"
  }
}

resource "aws_route" "public_internet_access" {
  route_table_id         = aws_route_table.public.id
  destination_cidr_block = "0.0.0.0/0"
  gateway_id             = aws_internet_gateway.main.id
}

resource "aws_route_table_association" "public" {
  count          = length(var.public_subnet_cidrs)
  subnet_id      = aws_subnet.public[count.index].id
  route_table_id = aws_route_table.public.id
}

data "aws_availability_zones" "available" {}

resource "aws_security_group" "mlflow" {
  name        = "mlflow-sg"
  description = "Allow HTTP, HTTPS, and PostgreSQL access"
  vpc_id      = aws_vpc.main.id

  ingress {
    description = "Allow HTTP"
    from_port   = 80
    to_port     = 80
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
  }

  ingress {
    description = "Allow HTTPS"
    from_port   = 443
    to_port     = 443
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
  }

  ingress {
    description = "Allow MLflow UI/API"
    from_port   = 5000
    to_port     = 5000
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
  }

  ingress {
    description = "Allow PostgreSQL"
    from_port   = 5432
    to_port     = 5432
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "mlflow-sg"
  }
}

resource "aws_s3_bucket" "mlflow_artifacts" {
  bucket = var.mlflow_s3_bucket_name
  force_destroy = true
  tags = {
    Name = "mlflow-artifacts"
  }
}

resource "aws_s3_bucket_public_access_block" "mlflow_artifacts" {
  bucket = aws_s3_bucket.mlflow_artifacts.id
  block_public_acls   = true
  block_public_policy = true
  ignore_public_acls  = true
  restrict_public_buckets = true
}

data "aws_iam_policy_document" "mlflow_s3_access" {
  statement {
    actions = [
      "s3:GetObject",
      "s3:PutObject",
      "s3:ListBucket"
    ]
    resources = [
      aws_s3_bucket.mlflow_artifacts.arn,
      "${aws_s3_bucket.mlflow_artifacts.arn}/*"
    ]
  }
}

resource "aws_iam_role" "mlflow_apprunner" {
  name = "mlflow-apprunner-role"
  assume_role_policy = jsonencode({
    Version = "2012-10-17"
    Statement = [{
      Effect = "Allow"
      Principal = {
        Service = "build.apprunner.amazonaws.com"
      }
      Action = "sts:AssumeRole"
    }]
  })
}

resource "aws_iam_policy" "mlflow_s3_access" {
  name   = "mlflow-s3-access"
  policy = data.aws_iam_policy_document.mlflow_s3_access.json
}

resource "aws_iam_role_policy_attachment" "mlflow_s3_access" {
  role       = aws_iam_role.mlflow_apprunner.name
  policy_arn = aws_iam_policy.mlflow_s3_access.arn
}

resource "aws_apprunner_service" "mlflow" {
  service_name = "mlflow-apprunner"
  source_configuration {
    image_repository {
      image_identifier      = var.mlflow_container_image
      image_repository_type = "ECR"
      image_configuration {
        port = "5000"
        runtime_environment_variables = {
          MLFLOW_S3_BUCKET     = aws_s3_bucket.mlflow_artifacts.bucket
          MLFLOW_TRACKING_URI = "postgresql://${var.db_username}:${var.db_password}@${aws_db_instance.mlflow.address}:5432/mlflow"
        }
      }
    }
    auto_deployments_enabled = false
    authentication_configuration {
      access_role_arn = aws_iam_role.mlflow_apprunner.arn
    }
  }
  instance_configuration {
    cpu    = "1024"
    memory = "2048"
  }
  tags = {
    Name = "mlflow-apprunner"
  }
}

output "mlflow_apprunner_url" {
  description = "URL of the MLflow Tracking Server (App Runner)"
  value       = aws_apprunner_service.mlflow.service_url
}

output "mlflow_s3_bucket" {
  description = "S3 bucket for MLflow artifacts"
  value       = aws_s3_bucket.mlflow_artifacts.bucket
}

output "mlflow_rds_endpoint" {
  description = "RDS endpoint for MLflow metadata storage"
  value       = aws_db_instance.mlflow.address
}
