plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Developer Workstation" as dev {
  artifact "Python + Shell" as pysh
  artifact "Ansible" as ans
  artifact "Terraform CLI" as tf
  artifact "CLI Tools (crictl, flux, helm, k9s, kubectl, kubectx)" as clitools
}

node "GitHub Actions" as gha {
  artifact "tfsec Security Scanning" as tfsec
}

node "Kubernetes Cluster (RKE)" as k8s {
  artifact "Kubernetes API" as k8sapi
}

cloud "Remote Infrastructure" as remote {
  artifact "Terraform-managed Resources" as tfres
}

' Relationships
pysh --> ans : Runs playbooks
ans --> clitools : Installs/Manages
ans --> tf : Provisions infra
ans --> k8s : Deploys/Manages
clitools --> k8sapi : Interacts with
ans --> k8sapi : Manages cluster

' GitHub Actions
gha --> tfsec : Runs tfsec
gha --> tf : Triggers infra scan

tf --> tfres : Provisions

@enduml
