terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 6.27.0"
    }
  }
  required_version = ">= 1.3.0"
}

provider "aws" {
  region = var.aws_region
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "eu-west-1"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDRs."
  type        = list(string)
  default     = ["10.0.1.0/24", "10.0.2.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDRs."
  type        = list(string)
  default     = ["10.0.101.0/24", "10.0.102.0/24"]
}

variable "db_username" {
  description = "Username for the RDS PostgreSQL instance."
  type        = string
  default     = "pgadmin"
}

variable "db_password" {
  description = "Password for the RDS PostgreSQL instance."
  type        = string
  sensitive   = true
}

variable "clickhouse_instance_type" {
  description = "EC2 instance type for Clickhouse."
  type        = string
  default     = "t3.large"
}

variable "clickhouse_key_pair" {
  description = "Name of the EC2 Key Pair for Clickhouse SSH access."
  type        = string
}

output "vpc_id" {
  value = aws_vpc.main.id
}

output "public_subnet_ids" {
  value = aws_subnet.public[*].id
}

output "private_subnet_ids" {
  value = aws_subnet.private[*].id
}

output "rds_endpoint" {
  value = aws_db_instance.postgres.endpoint
}

output "sagemaker_notebook_url" {
  value = aws_sagemaker_notebook_instance.notebook.url
}

output "msk_bootstrap_brokers" {
  value = aws_msk_cluster.kafka.bootstrap_brokers
}

output "clickhouse_public_ip" {
  value = aws_instance.clickhouse.public_ip
}

output "s3_bucket_name" {
  value = aws_s3_bucket.dataset.bucket
}

resource "aws_vpc" "main" {
  cidr_block           = var.vpc_cidr
  enable_dns_support   = true
  enable_dns_hostnames = true
  tags = {
    Name = "main-vpc"
  }
}

resource "aws_internet_gateway" "gw" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "main-igw"
  }
}

resource "aws_subnet" "public" {
  count                   = length(var.public_subnet_cidrs)
  vpc_id                  = aws_vpc.main.id
  cidr_block              = var.public_subnet_cidrs[count.index]
  map_public_ip_on_launch = true
  availability_zone       = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "public-subnet-${count.index + 1}"
  }
}

resource "aws_subnet" "private" {
  count             = length(var.private_subnet_cidrs)
  vpc_id            = aws_vpc.main.id
  cidr_block        = var.private_subnet_cidrs[count.index]
  availability_zone = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "private-subnet-${count.index + 1}"
  }
}

resource "aws_route_table" "public" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "public-rt"
  }
}

resource "aws_route" "public_internet_access" {
  route_table_id         = aws_route_table.public.id
  destination_cidr_block = "0.0.0.0/0"
  gateway_id             = aws_internet_gateway.gw.id
}

resource "aws_route_table_association" "public" {
  count          = length(var.public_subnet_cidrs)
  subnet_id      = aws_subnet.public[count.index].id
  route_table_id = aws_route_table.public.id
}

resource "aws_eip" "nat" {
  # vpc = true # REMOVE THIS LINE, not supported in latest provider
}

resource "aws_nat_gateway" "nat" {
  allocation_id = aws_eip.nat.id
  subnet_id     = aws_subnet.public[0].id
  tags = {
    Name = "main-nat-gw"
  }
}

resource "aws_route_table" "private" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "private-rt"
  }
}

resource "aws_route" "private_nat_gateway" {
  route_table_id         = aws_route_table.private.id
  destination_cidr_block = "0.0.0.0/0"
  nat_gateway_id         = aws_nat_gateway.nat.id
}

resource "aws_route_table_association" "private" {
  count          = length(var.private_subnet_cidrs)
  subnet_id      = aws_subnet.private[count.index].id
  route_table_id = aws_route_table.private.id
}

data "aws_availability_zones" "available" {}

resource "aws_security_group" "clickhouse" {
  name        = "clickhouse-sg"
  description = "Allow access to Clickhouse EC2 from trusted sources"
  vpc_id      = aws_vpc.main.id

  ingress {
    from_port   = 8123
    to_port     = 8123
    protocol    = "tcp"
    cidr_blocks = [aws_vpc.main.cidr_block]
  }

  ingress {
    from_port   = 9000
    to_port     = 9000
    protocol    = "tcp"
    cidr_blocks = [aws_vpc.main.cidr_block]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "clickhouse-sg"
  }
}

resource "aws_security_group" "rds" {
  name        = "rds-sg"
  description = "Allow access to RDS from VPC"
  vpc_id      = aws_vpc.main.id

  ingress {
    from_port   = 5432
    to_port     = 5432
    protocol    = "tcp"
    cidr_blocks = [aws_vpc.main.cidr_block]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "rds-sg"
  }
}

resource "aws_security_group" "sagemaker" {
  name        = "sagemaker-sg"
  description = "Allow SageMaker notebook access to VPC resources"
  vpc_id      = aws_vpc.main.id

  ingress {
    from_port   = 0
    to_port     = 65535
    protocol    = "tcp"
    cidr_blocks = [aws_vpc.main.cidr_block]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "sagemaker-sg"
  }
}

resource "aws_s3_bucket" "dataset" {
  bucket = "public-dataset-${random_id.suffix.hex}"
  force_destroy = true
  tags = {
    Name = "public-dataset"
  }
}

resource "random_id" "suffix" {
  byte_length = 4
}

resource "aws_s3_bucket_public_access_block" "dataset" {
  bucket = aws_s3_bucket.dataset.id
  block_public_acls   = true
  block_public_policy = true
  ignore_public_acls  = true
  restrict_public_buckets = true
}

resource "aws_db_subnet_group" "postgres" {
  name       = "postgres-subnet-group"
  subnet_ids = aws_subnet.private[*].id
  tags = {
    Name = "postgres-subnet-group"
  }
}

resource "aws_db_instance" "postgres" {
  identifier              = "analytics-postgres"
  engine                  = "postgres"
  instance_class          = "db.t3.medium"
  allocated_storage       = 20
  max_allocated_storage   = 100
  db_subnet_group_name    = aws_db_subnet_group.postgres.name
  vpc_security_group_ids  = [aws_security_group.rds.id]
  username                = var.db_username
  password                = var.db_password
  skip_final_snapshot     = true
  publicly_accessible     = false
  multi_az                = false
  storage_encrypted       = true
  backup_retention_period = 7
  tags = {
    Name = "analytics-postgres"
  }
}

resource "aws_instance" "clickhouse" {
  ami                    = data.aws_ami.ubuntu.id
  instance_type          = var.clickhouse_instance_type
  subnet_id              = aws_subnet.private[0].id
  vpc_security_group_ids = [aws_security_group.clickhouse.id]
  key_name               = var.clickhouse_key_pair
  associate_public_ip_address = true
  root_block_device {
    volume_size = 100
    volume_type = "gp3"
  }
  tags = {
    Name = "clickhouse-server"
  }
}

data "aws_ami" "ubuntu" {
  most_recent = true
  owners      = ["099720109477"] # Canonical
  filter {
    name   = "name"
    values = ["ubuntu/images/hvm-ssd/ubuntu-focal-20.04-amd64-server-*"]
  }
}

resource "aws_iam_role" "sagemaker_execution" {
  name = "sagemaker-execution-role"
  assume_role_policy = data.aws_iam_policy_document.sagemaker_assume_role.json
}

data "aws_iam_policy_document" "sagemaker_assume_role" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["sagemaker.amazonaws.com"]
    }
  }
}

resource "aws_iam_role_policy_attachment" "sagemaker_s3" {
  role       = aws_iam_role.sagemaker_execution.name
  policy_arn = "arn:aws:iam::aws:policy/AmazonS3ReadOnlyAccess"
}

resource "aws_sagemaker_notebook_instance" "notebook" {
  name                 = "analytics-notebook"
  instance_type        = "ml.t3.medium"
  role_arn             = aws_iam_role.sagemaker_execution.arn
  subnet_id            = aws_subnet.private[0].id
  security_groups      = [aws_security_group.sagemaker.id]
  lifecycle_config_name = null
  tags = {
    Name = "analytics-notebook"
  }
}

resource "aws_msk_cluster" "kafka" {
  cluster_name           = "analytics-msk"
  kafka_version          = "3.4.0"
  number_of_broker_nodes = 2
  broker_node_group_info {
    instance_type   = "kafka.m5.large"
    client_subnets  = aws_subnet.private[*].id
    security_groups = [aws_security_group.clickhouse.id]
  }
  encryption_info {
    encryption_in_transit {
      client_broker = "TLS"
      in_cluster    = true
    }
  }
  tags = {
    Name = "analytics-msk"
  }
}

resource "aws_iam_role" "kinesis_analytics" {
  name = "kinesis-analytics-role"
  assume_role_policy = data.aws_iam_policy_document.kinesis_assume_role.json
}

data "aws_iam_policy_document" "kinesis_assume_role" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["kinesisanalytics.amazonaws.com"]
    }
  }
}

resource "aws_iam_role_policy_attachment" "kinesis_full" {
  role       = aws_iam_role.kinesis_analytics.name
  policy_arn = "arn:aws:iam::aws:policy/AmazonKinesisFullAccess"
}

# NOTE: The aws_kinesis_analytics_application resource block is commented out due to validation errors. Please refer to the AWS provider documentation for the correct arguments for Flink on Kinesis Data Analytics.
# resource "aws_kinesis_analytics_application" "flink" {
#   name        = "analytics-flink"
#   # runtime_environment = "FLINK-1_15" # Not supported in this resource
#   # service_execution_role = aws_iam_role.kinesis_analytics.arn # Not supported in this resource
#   # cloudwatch_logging_options { ... } # role_arn is required, but not supported here
#   tags = {
#     Name = "analytics-flink"
#   }
# }

resource "aws_cloudwatch_log_group" "flink" {
  name = "/aws/kinesis-analytics/analytics-flink"
}

resource "aws_cloudwatch_log_stream" "flink" {
  name           = "flink-log-stream"
  log_group_name = aws_cloudwatch_log_group.flink.name
}

resource "aws_iam_role" "clickhouse_ec2" {
  name = "clickhouse-ec2-role"
  assume_role_policy = data.aws_iam_policy_document.ec2_assume_role.json
}

data "aws_iam_policy_document" "ec2_assume_role" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["ec2.amazonaws.com"]
    }
  }
}

resource "aws_iam_role_policy_attachment" "ec2_s3" {
  role       = aws_iam_role.clickhouse_ec2.name
  policy_arn = "arn:aws:iam::aws:policy/AmazonS3ReadOnlyAccess"
}
