plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Docker Container" as docker {
  node "Flask Web App\n(Python, Flask, Gunicorn, Gevent)" as flaskapp
}

cloud "AWS ECS" as ecs
cloud "Docker Registry" as registry

flaskapp -down-> docker : "Packaged in"
docker -down-> ecs : "Deployed to"
docker -right-> registry : "Pushed to"

@enduml
