plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Linux Host (dev01/stage01)" as linuxHost {
  node "Docker Engine" as dockerEngine {
    node "Tomcat Server\n(Java, Servlet API)" as tomcat {
      artifact "Java Web App (WAR)" as war
    }
  }
}

cloud "Docker Container Registry" as registry

actor "DevOps Engineer" as devops

component "Ansible Playbook" as ansible

' Relationships
war -- tomcat : Deployed on
 tomcat -- dockerEngine : Runs inside
 dockerEngine -- linuxHost : Runs on
 dockerEngine ..> registry : Pull/Push Images
ansible ..> dockerEngine : Deploys/Manages
ansible ..> registry : Pulls Images
ansible ..> linuxHost : Connects via SSH
 devops --> ansible : Triggers Deployment
@enduml
