plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

' Deployment diagram for MkDocs static site with S3 and Terraform-managed infra
node "MkDocs Site Builder" as mkdocs {
  [MkDocs]
  [Python]
  [Shell]
  [MkDocs Plugins]
}

node "Docker Container" as docker {
  [mkdocs:latest]
}

cloud "AWS Cloud" {
  node "S3 Bucket (Docs Artifacts)" as s3 {
    [Amazon S3]
  }
  node "Terraform State S3 Bucket" as tfstate {
    [Amazon S3]
  }
}

node "Infra Provisioner" as infra {
  [Terraform]
  [Shell]
}

mkdocs -down-> docker : "Builds static site in container"
docker -down-> s3 : "Uploads built site to S3"
infra -down-> tfstate : "Stores state in S3"
infra -right-> s3 : "Provisions S3 bucket for docs"

@enduml
