plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Docker Host" {
  node "Minecraft Server (Lobby)\n[itzg/minecraft-server]\nDocker Container" as minecraft_server {
    artifact "minecraft-server.jar" <<YAML, Docker Compose>>
  }
  node "RCON Server\n[itzg/rcon]\nDocker Container" as rcon_server {
    artifact "rcon-server" <<YAML, Docker Compose>>
  }
}

minecraft_server -[hidden]-> rcon_server
rcon_server --> minecraft_server : RCON Protocol
@enduml
