files.content:
terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = ">= 5.0"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDRs."
  type        = list(string)
  default     = ["10.0.1.0/24", "10.0.2.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDRs."
  type        = list(string)
  default     = ["10.0.101.0/24", "10.0.102.0/24"]
}

variable "minecraft_server_name" {
  description = "Name for the Minecraft ECS service."
  type        = string
  default     = "minecraft-lobby"
}

variable "rcon_server_name" {
  description = "Name for the RCON ECS service."
  type        = string
  default     = "minecraft-rcon"
}

variable "efs_throughput_mode" {
  description = "EFS throughput mode."
  type        = string
  default     = "bursting"
}

variable "efs_performance_mode" {
  description = "EFS performance mode."
  type        = string
  default     = "generalPurpose"
}

variable "minecraft_image" {
  description = "Docker image for Minecraft server."
  type        = string
  default     = "itzg/minecraft-server:latest"
}

variable "rcon_image" {
  description = "Docker image for RCON server."
  type        = string
  default     = "itzg/rcon:latest"
}

variable "minecraft_port" {
  description = "Port for Minecraft server."
  type        = number
  default     = 25565
}

variable "rcon_port" {
  description = "Port for RCON server."
  type        = number
  default     = 25575
}

resource "aws_vpc" "main" {
  cidr_block           = var.vpc_cidr
  enable_dns_support   = true
  enable_dns_hostnames = true
  tags = {
    Name = "minecraft-vpc"
  }
}

resource "aws_internet_gateway" "main" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "minecraft-igw"
  }
}

resource "aws_subnet" "public" {
  count                   = length(var.public_subnet_cidrs)
  vpc_id                  = aws_vpc.main.id
  cidr_block              = var.public_subnet_cidrs[count.index]
  map_public_ip_on_launch = true
  availability_zone       = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "minecraft-public-${count.index + 1}"
  }
}

resource "aws_subnet" "private" {
  count             = length(var.private_subnet_cidrs)
  vpc_id            = aws_vpc.main.id
  cidr_block        = var.private_subnet_cidrs[count.index]
  availability_zone = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "minecraft-private-${count.index + 1}"
  }
}

resource "aws_route_table" "public" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "minecraft-public-rt"
  }
}

resource "aws_route" "public_internet_access" {
  route_table_id         = aws_route_table.public.id
  destination_cidr_block = "0.0.0.0/0"
  gateway_id             = aws_internet_gateway.main.id
}

resource "aws_route_table_association" "public" {
  count          = length(var.public_subnet_cidrs)
  subnet_id      = aws_subnet.public[count.index].id
  route_table_id = aws_route_table.public.id
}

resource "aws_efs_file_system" "minecraft" {
  performance_mode = var.efs_performance_mode
  throughput_mode  = var.efs_throughput_mode
  tags = {
    Name = "minecraft-efs"
  }
}

resource "aws_efs_mount_target" "minecraft" {
  count          = length(aws_subnet.private)
  file_system_id = aws_efs_file_system.minecraft.id
  subnet_id      = aws_subnet.private[count.index].id
  security_groups = [aws_security_group.efs.id]
}

resource "aws_security_group" "efs" {
  name        = "minecraft-efs-sg"
  description = "Allow NFS access from ECS tasks"
  vpc_id      = aws_vpc.main.id

  ingress {
    from_port   = 2049
    to_port     = 2049
    protocol    = "tcp"
    security_groups = [aws_security_group.ecs_tasks.id]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
}

resource "aws_ecs_cluster" "main" {
  name = "minecraft-cluster"
}

resource "aws_security_group" "ecs_tasks" {
  name        = "minecraft-ecs-tasks-sg"
  description = "Allow Minecraft and RCON traffic"
  vpc_id      = aws_vpc.main.id

  ingress {
    from_port   = var.minecraft_port
    to_port     = var.minecraft_port
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
    description = "Allow Minecraft client access"
  }

  ingress {
    from_port   = var.rcon_port
    to_port     = var.rcon_port
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
    description = "Allow RCON access"
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
}

resource "aws_iam_role" "ecs_task_execution" {
  name = "ecsTaskExecutionRole"
  assume_role_policy = data.aws_iam_policy_document.ecs_task_assume_role_policy.json
}

data "aws_iam_policy_document" "ecs_task_assume_role_policy" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["ecs-tasks.amazonaws.com"]
    }
  }
}

resource "aws_iam_role_policy_attachment" "ecs_task_execution" {
  role       = aws_iam_role.ecs_task_execution.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AmazonECSTaskExecutionRolePolicy"
}

resource "aws_ecs_task_definition" "minecraft" {
  family                   = var.minecraft_server_name
  network_mode             = "awsvpc"
  requires_compatibilities = ["FARGATE"]
  cpu                      = "1024"
  memory                   = "2048"
  execution_role_arn       = aws_iam_role.ecs_task_execution.arn
  container_definitions    = jsonencode([
    {
      name      = var.minecraft_server_name
      image     = var.minecraft_image
      essential = true
      portMappings = [
        {
          containerPort = var.minecraft_port
          hostPort      = var.minecraft_port
          protocol      = "tcp"
        }
      ]
      mountPoints = [
        {
          sourceVolume  = "minecraft-data"
          containerPath = "/data"
        }
      ]
      logConfiguration = {
        logDriver = "awslogs"
        options = {
          awslogs-group         = aws_cloudwatch_log_group.minecraft.name
          awslogs-region        = var.aws_region
          awslogs-stream-prefix = "minecraft"
        }
      }
      environment = [
        {
          name  = "EULA"
          value = "TRUE"
        }
      ]
    }
  ])
  volume {
    name = "minecraft-data"
    efs_volume_configuration {
      file_system_id          = aws_efs_file_system.minecraft.id
      root_directory          = "/"
      transit_encryption      = "ENABLED"
      authorization_config {
        access_point_id = null
        iam             = "DISABLED"
      }
    }
  }
}

resource "aws_ecs_task_definition" "rcon" {
  family                   = var.rcon_server_name
  network_mode             = "awsvpc"
  requires_compatibilities = ["FARGATE"]
  cpu                      = "256"
  memory                   = "512"
  execution_role_arn       = aws_iam_role.ecs_task_execution.arn
  container_definitions    = jsonencode([
    {
      name      = var.rcon_server_name
      image     = var.rcon_image
      essential = true
      portMappings = [
        {
          containerPort = var.rcon_port
          hostPort      = var.rcon_port
          protocol      = "tcp"
        }
      ]
      logConfiguration = {
        logDriver = "awslogs"
        options = {
          awslogs-group         = aws_cloudwatch_log_group.rcon.name
          awslogs-region        = var.aws_region
          awslogs-stream-prefix = "rcon"
        }
      }
    }
  ])
}

resource "aws_ecs_service" "minecraft" {
  name            = var.minecraft_server_name
  cluster         = aws_ecs_cluster.main.id
  task_definition = aws_ecs_task_definition.minecraft.arn
  desired_count   = 1
  launch_type     = "FARGATE"
  network_configuration {
    subnets          = aws_subnet.public[*].id
    security_groups  = [aws_security_group.ecs_tasks.id]
    assign_public_ip = true
  }
  depends_on = [aws_efs_mount_target.minecraft]
}

resource "aws_ecs_service" "rcon" {
  name            = var.rcon_server_name
  cluster         = aws_ecs_cluster.main.id
  task_definition = aws_ecs_task_definition.rcon.arn
  desired_count   = 1
  launch_type     = "FARGATE"
  network_configuration {
    subnets          = aws_subnet.public[*].id
    security_groups  = [aws_security_group.ecs_tasks.id]
    assign_public_ip = true
  }
}

resource "aws_cloudwatch_log_group" "minecraft" {
  name              = "/ecs/minecraft"
  retention_in_days = 14
}

resource "aws_cloudwatch_log_group" "rcon" {
  name              = "/ecs/rcon"
  retention_in_days = 14
}

output "minecraft_server_public_ip" {
  description = "Public IPs of the Minecraft server tasks."
  value = aws_ecs_service.minecraft.network_configuration[0].assign_public_ip
}

output "rcon_server_public_ip" {
  description = "Public IPs of the RCON server tasks."
  value = aws_ecs_service.rcon.network_configuration[0].assign_public_ip
}

output "efs_id" {
  description = "EFS File System ID for Minecraft data."
  value       = aws_efs_file_system.minecraft.id
}
