plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Web Application\nExpress/Node.js" as webapp {
  artifact "app.js" as appjs
}

node "Docker Container" as docker {
  artifact "Dockerfile"
  artifact "Makefile"
}

node "CI/CD Pipeline\nGitHub Actions" as cicd {
  artifact "build.yml"
}

cloud "Potential Cloud Infrastructure\n(Terraform-managed)" as cloud {
  artifact "terraform.yml"
}

webapp -down-> docker : "Containerized via Dockerfile"
docker -down-> cicd : "Built/Deployed by CI/CD"
cicd -down-> cloud : "(Potential) Infra Provisioning"
@enduml
