plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "AWS Lambda Function\n(Rust, AWS Lambda Rust Runtime)" as lambdaFn {
  artifact "lambda.zip" as lambdaZip
}

node "Delta Lake Table\n(delta-rs)" as deltaLake

cloud "Amazon S3 Bucket" as s3

lambdaFn --> s3 : Reads/Writes data (rusoto_s3)
lambdaFn --> deltaLake : Reads/Writes tables (delta-rs)
deltaLake --> s3 : Stores Parquet files

@enduml
