terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 6.27.0"
    }
  }
  required_version = ">= 1.3.0"
}

provider "aws" {
  region = var.aws_region
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "lambda_function_name" {
  description = "Name for the Lambda function."
  type        = string
  default     = "rust-delta-lake-lambda"
}

variable "s3_bucket_name" {
  description = "Name for the S3 bucket to store Delta Lake tables."
  type        = string
}

variable "vpc_id" {
  description = "VPC ID for Lambda networking."
  type        = string
  default     = null
}

variable "subnet_ids" {
  description = "List of subnet IDs for Lambda networking."
  type        = list(string)
  default     = []
}

resource "aws_s3_bucket" "delta_lake" {
  bucket = var.s3_bucket_name

  force_destroy = true

  tags = {
    Name = var.s3_bucket_name
    Environment = "dev"
  }
}

resource "aws_s3_bucket_public_access_block" "delta_lake" {
  bucket = aws_s3_bucket.delta_lake.id

  block_public_acls   = true
  block_public_policy = true
  ignore_public_acls  = true
  restrict_public_buckets = true
}

data "aws_iam_policy_document" "lambda_assume_role" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["lambda.amazonaws.com"]
    }
  }
}

resource "aws_iam_role" "lambda_exec" {
  name               = "lambda-delta-lake-exec-role"
  assume_role_policy = data.aws_iam_policy_document.lambda_assume_role.json
}

resource "aws_iam_policy" "lambda_s3_policy" {
  name        = "lambda-delta-lake-s3-policy"
  description = "IAM policy for Lambda to access S3 and CloudWatch Logs."
  policy      = jsonencode({
    Version = "2012-10-17",
    Statement = [
      {
        Effect = "Allow",
        Action = [
          "s3:GetObject",
          "s3:PutObject",
          "s3:ListBucket"
        ],
        Resource = [
          aws_s3_bucket.delta_lake.arn,
          "${aws_s3_bucket.delta_lake.arn}/*"
        ]
      },
      {
        Effect = "Allow",
        Action = [
          "logs:CreateLogGroup",
          "logs:CreateLogStream",
          "logs:PutLogEvents"
        ],
        Resource = "arn:aws:logs:*:*:*"
      }
    ]
  })
}

resource "aws_iam_role_policy_attachment" "lambda_s3_attach" {
  role       = aws_iam_role.lambda_exec.name
  policy_arn = aws_iam_policy.lambda_s3_policy.arn
}

resource "aws_lambda_function" "rust_delta_lake" {
  function_name = var.lambda_function_name
  role          = aws_iam_role.lambda_exec.arn
  handler       = "bootstrap"
  runtime       = "provided.al2"
  filename      = "${path.module}/.dummy/lambda.zip"
  source_code_hash = filebase64sha256("${path.module}/.dummy/lambda.zip")

  environment {
    variables = {
      S3_BUCKET = aws_s3_bucket.delta_lake.bucket
    }
  }

  vpc_config {
    subnet_ids         = var.subnet_ids
    security_group_ids = [aws_security_group.lambda_sg.id]
  }

  depends_on = [aws_iam_role_policy_attachment.lambda_s3_attach]
}

resource "aws_security_group" "lambda_sg" {
  name        = "lambda-delta-lake-sg"
  description = "Security group for Lambda function"
  vpc_id      = var.vpc_id

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
}

output "lambda_function_name" {
  value = aws_lambda_function.rust_delta_lake.function_name
}

output "s3_bucket_name" {
  value = aws_s3_bucket.delta_lake.bucket
}

output "lambda_role_arn" {
  value = aws_iam_role.lambda_exec.arn
}
