@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Web Application\n(Pynecone, Python)" as WebApp {
  artifact "Pynecone App" as App
}

node "Google APIs" as GoogleAPIs {
  artifact "Gmail API\n(google-api-python-client, google-auth-oauthlib)" as GmailAPI
}

database "SQLite DB\n(pynecone.db)" as SQLiteDB

WebApp --> GoogleAPIs : Uses Gmail API
WebApp --> SQLiteDB : Reads/Writes Data

@enduml
