plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

' Deployment diagram for Terraform-based AWS Squid Proxy setup
node "DevOps Workstation" as devops {
  artifact "Docker Container\n(Terraform 1.4.5)" as docker
  artifact "Shell Scripts" as shell
}

node "AWS Cloud" as aws {
  node "VPC" as vpc {
    node "EC2 Instance\n(Squid Proxy)" as ec2
  }
  artifact "IAM Role/Policy" as iam
  artifact "SSH Keypair" as ssh
}

shell -down-> docker : runs in

docker -right-> iam : uses AWS credentials

docker -right-> ssh : uses for EC2 access

docker -right-> vpc : provisions via Terraform
vpc -down-> ec2 : hosts

ec2 -left-> ssh : accessed via

@enduml
