plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Docker Container" as docker {
  node "Rust Web Server" as rust_server <<Rust, actix-web, actix-files>> {
    [actix-web API]
    [Static File Server]
  }
}

actor "User" as user

user --> [actix-web API] : HTTP Request
user --> [Static File Server] : HTTP Request (Static Files)
[actix-web API] --> [Static File Server] : Serves static files

@enduml
