plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Kubernetes Cluster" as k8s {
  node "Kubernetes Node" as k8s_node {
    node "Tomcat Container\n(Java, JSP, Maven)" as tomcat {
      artifact "Web Application (WAR)\n[Log4j, Servlet API]" as webapp
    }
  }
  component "Kubernetes Deployment" as k8s_deploy
  component "NodePort Service" as k8s_nodeport
}

cloud "CI/CD Pipeline" as cicd {
  node "Jenkins Master" as jenkins_master
  node "Jenkins Slave" as jenkins_slave
}

folder "Automation" as automation {
  component "Ansible Playbooks" as ansible
}

webapp -down-> tomcat : "Deployed in"
tomcat -down-> k8s_node : "Runs on"
k8s_node -down-> k8s : "Part of"
k8s_deploy -down-> k8s_node : "Manages Pod"
k8s_nodeport -down-> k8s : "Exposes Service"
cicd -down-> jenkins_master : "Jenkins Master"
jenkins_master -down-> jenkins_slave : "Delegates Jobs"
jenkins_master -down-> ansible : "Triggers Automation"
ansible -down-> k8s : "Deploys/Configures"
cicd -down-> k8s : "Deploys via Pipeline"

@enduml
