plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "User" as user

node "AWS EC2\n(Jenkins Master)" as jenkins_master <<EC2>> {
  component "Jenkins Master\n(Java, Jenkins)" as jenkins
}

node "AWS EC2\n(Jenkins Slave)" as jenkins_slave <<EC2>> {
  component "Jenkins Slave\n(Java, Jenkins Agent)" as jenkinsagent
}

node "Kubernetes Cluster" as k8s {
  node "Tomcat 8.0\n(Docker Container)" as tomcat {
    artifact "Web Application\n(Java WAR, Servlet API)" as webapp
  }
  component "Prometheus" as prometheus
  component "Grafana" as grafana
}

user --> webapp : HTTP Requests
webapp --> tomcat : Deployed on
webapp --> prometheus : Exposes metrics
prometheus --> grafana : Metrics visualization
jenkins --> jenkinsagent : Triggers builds/deployments
jenkinsagent --> k8s : Deploys WAR to Tomcat

@enduml
