files.content:
terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = ">= 5.0"
    }
    kubernetes = {
      source  = "hashicorp/kubernetes"
      version = ">= 2.20.0"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
  token                  = data.aws_eks_cluster_auth.cluster.token
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDRs."
  type        = list(string)
  default     = ["10.0.1.0/24", "10.0.2.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDRs."
  type        = list(string)
  default     = ["10.0.101.0/24", "10.0.102.0/24"]
}

variable "eks_cluster_name" {
  description = "Name for the EKS cluster."
  type        = string
  default     = "flask-activemq-eks"
}

variable "node_instance_type" {
  description = "EC2 instance type for EKS nodes."
  type        = string
  default     = "t3.medium"
}

variable "desired_capacity" {
  description = "Desired number of worker nodes."
  type        = number
  default     = 2
}

variable "max_capacity" {
  description = "Maximum number of worker nodes."
  type        = number
  default     = 3
}

variable "min_capacity" {
  description = "Minimum number of worker nodes."
  type        = number
  default     = 1
}

module "eks" {
  source          = "terraform-aws-modules/eks/aws"
  version         = "20.8.4"

  cluster_name    = var.eks_cluster_name
  cluster_version = "1.29"
  vpc_id          = module.vpc.vpc_id
  subnet_ids      = module.vpc.private_subnets

  enable_irsa = true

  eks_managed_node_group_defaults = {
    ami_type       = "AL2_x86_64"
    instance_type  = var.node_instance_type
    min_size       = var.min_capacity
    max_size       = var.max_capacity
    desired_size   = var.desired_capacity
  }

  eks_managed_node_groups = {
    default = {
      min_size     = var.min_capacity
      max_size     = var.max_capacity
      desired_size = var.desired_capacity
      instance_type = var.node_instance_type
      name         = "default"
    }
  }

  tags = {
    Name = "flask-activemq-eks"
  }
}

data "aws_eks_cluster" "cluster" {
  name = module.eks.cluster_name
}

data "aws_eks_cluster_auth" "cluster" {
  name = module.eks.cluster_name
}

resource "aws_ecr_repository" "flask_app" {
  name = "flask-app"
  image_scanning_configuration {
    scan_on_push = true
  }
  tags = {
    Name = "flask-app"
  }
}

output "vpc_id" {
  value = module.vpc.vpc_id
}

output "eks_cluster_name" {
  value = module.eks.cluster_name
}

output "eks_cluster_endpoint" {
  value = module.eks.cluster_endpoint
}

output "ecr_repository_url" {
  value = aws_ecr_repository.flask_app.repository_url
}

resource "kubernetes_deployment" "flask_app" {
  metadata {
    name      = "flask-app"
    namespace = "default"
    labels = {
      app = "flask-app"
    }
  }
  spec {
    replicas = 2
    selector {
      match_labels = {
        app = "flask-app"
      }
    }
    template {
      metadata {
        labels = {
          app = "flask-app"
        }
      }
      spec {
        container {
          name  = "flask-app"
          image = aws_ecr_repository.flask_app.repository_url
          port {
            container_port = 5000
          }
        }
      }
    }
  }
}

resource "kubernetes_service" "flask_app_lb" {
  metadata {
    name      = "flask-app-lb"
    namespace = "default"
    labels = {
      app = "flask-app"
    }
  }
  spec {
    selector = {
      app = "flask-app"
    }
    port {
      port        = 80
      target_port = 5000
    }
    type = "LoadBalancer"
  }
}

resource "kubernetes_deployment" "activemq" {
  metadata {
    name      = "activemq"
    namespace = "default"
    labels = {
      app = "activemq"
    }
  }
  spec {
    replicas = 1
    selector {
      match_labels = {
        app = "activemq"
      }
    }
    template {
      metadata {
        labels = {
          app = "activemq"
        }
      }
      spec {
        container {
          name  = "activemq"
          image = "rmohr/activemq:5.16.3"
          port {
            container_port = 61616
          }
          port {
            container_port = 8161
          }
        }
      }
    }
  }
}

resource "kubernetes_service" "activemq" {
  metadata {
    name      = "activemq"
    namespace = "default"
    labels = {
      app = "activemq"
    }
  }
  spec {
    selector = {
      app = "activemq"
    }
    port {
      port        = 61616
      target_port = 61616
    }
    port {
      port        = 8161
      target_port = 8161
    }
    type = "ClusterIP"
  }
}
