plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Docker Host" {
  node "Flask API\n(Python + Flask)" as flask_api {
    [Flask App]
  }
  node "Localstack\n(AWS Service Simulator)" as localstack {
    [Localstack]
    cloud "AWS S3\n(Simulated)" as s3
  }
}

[Flask App] --> [Localstack] : Uses boto3
[Localstack] --> s3 : Simulates S3

@enduml
