plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Developer Workstation" as dev {
  artifact "Source Code" as src
}

node "GitHub" as github {
  component "GitHub Actions\n(CI/CD)" as cicd
}

node "Build/Deploy Host" as buildhost {
  component "Ansible\n(YAML, Shell)" as ansible
  component "Docker Engine" as docker
  component ".NET Core Runtime" as dotnet
  component "Java Runtime" as java
  component "AWS CLI" as awscli
}

node "Infrastructure as Code" as iac {
  component "Terraform" as terraform
}

node "AWS Cloud" as aws {
  node "ECS Cluster" as ecs {
    node "Container: WebApp" as webapp {
      component "Apache HTTPD\n(HTML, Apache config)" as apache
    }
  }
}

src --> github : Push code
cicd --> buildhost : Trigger build/deploy
ansible --> docker : Orchestrates container build
ansible --> awscli : Deploys to AWS
ansible --> dotnet
ansible --> java
cicd --> terraform : Infra provisioning
terraform --> aws : Provisions resources
buildhost --> aws : Deploys containers
awscli --> aws : Manages resources

docker --> webapp : Runs container
webapp --> apache : Hosts frontend

@enduml
