plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "RenovateBot Container" as renovateBot <<Docker, RenovateBot>> {
  artifact "ghcr.io/renovatebot/renovate:36.72.0" as image
}

artifact "renovate.json" as config
artifact "Dockerfile" as dockerfile

renovateBot -down-> image : Uses
renovateBot -down-> config : Reads
renovateBot -down-> dockerfile : Built from

@enduml
