files.content:
terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = ">= 4.0"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of CIDR blocks for public subnets."
  type        = list(string)
  default     = ["10.0.1.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of CIDR blocks for private subnets."
  type        = list(string)
  default     = ["10.0.2.0/24"]
}

variable "renovatebot_image" {
  description = "Docker image for RenovateBot."
  type        = string
  default     = "ghcr.io/renovatebot/renovate:36.72.0"
}

variable "renovatebot_task_cpu" {
  description = "CPU units for the RenovateBot ECS task."
  type        = number
  default     = 256
}

variable "renovatebot_task_memory" {
  description = "Memory (in MiB) for the RenovateBot ECS task."
  type        = number
  default     = 512
}

variable "renovatebot_schedule_expression" {
  description = "(Optional) Schedule expression for running RenovateBot (e.g., cron). Leave empty to run as a service."
  type        = string
  default     = ""
}

variable "renovatebot_secret_arn" {
  description = "ARN of the Secrets Manager secret containing renovate.json."
  type        = string
  default     = ""
}

resource "aws_vpc" "main" {
  cidr_block           = var.vpc_cidr
  enable_dns_support   = true
  enable_dns_hostnames = true
  tags = {
    Name = "renovatebot-vpc"
  }
}

resource "aws_internet_gateway" "main" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "renovatebot-igw"
  }
}

resource "aws_subnet" "public" {
  count                   = length(var.public_subnet_cidrs)
  vpc_id                  = aws_vpc.main.id
  cidr_block              = var.public_subnet_cidrs[count.index]
  map_public_ip_on_launch = true
  availability_zone       = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "renovatebot-public-${count.index}"
  }
}

resource "aws_subnet" "private" {
  count             = length(var.private_subnet_cidrs)
  vpc_id            = aws_vpc.main.id
  cidr_block        = var.private_subnet_cidrs[count.index]
  availability_zone = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "renovatebot-private-${count.index}"
  }
}

resource "aws_nat_gateway" "main" {
  allocation_id = aws_eip.nat.id
  subnet_id     = aws_subnet.public[0].id
  tags = {
    Name = "renovatebot-nat"
  }
}

resource "aws_eip" "nat" {
  domain = "vpc"
}

resource "aws_route_table" "public" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "renovatebot-public-rt"
  }
}

resource "aws_route" "public_internet_access" {
  route_table_id         = aws_route_table.public.id
  destination_cidr_block = "0.0.0.0/0"
  gateway_id             = aws_internet_gateway.main.id
}

resource "aws_route_table_association" "public" {
  count          = length(var.public_subnet_cidrs)
  subnet_id      = aws_subnet.public[count.index].id
  route_table_id = aws_route_table.public.id
}

resource "aws_route_table" "private" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "renovatebot-private-rt"
  }
}

resource "aws_route" "private_nat_gateway" {
  route_table_id         = aws_route_table.private.id
  destination_cidr_block = "0.0.0.0/0"
  nat_gateway_id         = aws_nat_gateway.main.id
}

resource "aws_route_table_association" "private" {
  count          = length(var.private_subnet_cidrs)
  subnet_id      = aws_subnet.private[count.index].id
  route_table_id = aws_route_table.private.id
}

data "aws_availability_zones" "available" {}

resource "aws_ecs_cluster" "main" {
  name = "renovatebot-cluster"
}

resource "aws_cloudwatch_log_group" "renovatebot" {
  name              = "/ecs/renovatebot"
  retention_in_days = 7
}

resource "aws_iam_role" "ecs_task_execution" {
  name = "renovatebot-ecs-task-execution-role"
  assume_role_policy = data.aws_iam_policy_document.ecs_task_assume_role_policy.json
}

data "aws_iam_policy_document" "ecs_task_assume_role_policy" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["ecs-tasks.amazonaws.com"]
    }
  }
}

resource "aws_iam_role_policy_attachment" "ecs_task_execution_policy" {
  role       = aws_iam_role.ecs_task_execution.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AmazonECSTaskExecutionRolePolicy"
}

resource "aws_iam_role_policy" "renovatebot_access" {
  name = "renovatebot-access-policy"
  role = aws_iam_role.ecs_task_execution.id
  policy = data.aws_iam_policy_document.renovatebot_access.json
}

data "aws_iam_policy_document" "renovatebot_access" {
  statement {
    actions = [
      "secretsmanager:GetSecretValue",
      "secretsmanager:DescribeSecret"
    ]
    resources = [var.renovatebot_secret_arn]
    effect    = "Allow"
  }
  statement {
    actions = [
      "logs:CreateLogStream",
      "logs:PutLogEvents"
    ]
    resources = ["*"]
    effect    = "Allow"
  }
}

resource "aws_security_group" "ecs_tasks" {
  name        = "renovatebot-ecs-tasks-sg"
  description = "Allow outbound access for ECS tasks"
  vpc_id      = aws_vpc.main.id
  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
  ingress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
    description = "Allow all ingress (adjust as needed)"
  }
  tags = {
    Name = "renovatebot-ecs-tasks-sg"
  }
}

resource "aws_ecs_task_definition" "renovatebot" {
  family                   = "renovatebot-task"
  requires_compatibilities = ["FARGATE"]
  network_mode             = "awsvpc"
  cpu                      = var.renovatebot_task_cpu
  memory                   = var.renovatebot_task_memory
  execution_role_arn       = aws_iam_role.ecs_task_execution.arn
  task_role_arn            = aws_iam_role.ecs_task_execution.arn
  container_definitions    = jsonencode([
    {
      name      = "renovatebot"
      image     = var.renovatebot_image
      essential = true
      environment = [
        {
          name  = "RENOVATE_CONFIG_FILE"
          value = "/tmp/renovate.json"
        }
      ]
      logConfiguration = {
        logDriver = "awslogs"
        options = {
          awslogs-group         = aws_cloudwatch_log_group.renovatebot.name
          awslogs-region        = var.aws_region
          awslogs-stream-prefix = "ecs"
        }
      }
      mountPoints = []
      secrets = [
        {
          name      = "RENOVATE_CONFIG_JSON"
          valueFrom = var.renovatebot_secret_arn
        }
      ]
    }
  ])
  runtime_platform {
    operating_system_family = "LINUX"
    cpu_architecture        = "X86_64"
  }
}

resource "aws_ecs_service" "renovatebot" {
  count               = var.renovatebot_schedule_expression == "" ? 1 : 0
  name                = "renovatebot-service"
  cluster             = aws_ecs_cluster.main.id
  task_definition     = aws_ecs_task_definition.renovatebot.arn
  desired_count       = 1
  launch_type         = "FARGATE"
  network_configuration {
    subnets          = aws_subnet.private[*].id
    security_groups  = [aws_security_group.ecs_tasks.id]
    assign_public_ip = false
  }
  depends_on = [aws_iam_role_policy_attachment.ecs_task_execution_policy]
}

resource "aws_cloudwatch_event_rule" "renovatebot_schedule" {
  count               = var.renovatebot_schedule_expression != "" ? 1 : 0
  name                = "renovatebot-schedule"
  schedule_expression = var.renovatebot_schedule_expression
}

resource "aws_cloudwatch_event_target" "renovatebot" {
  count     = var.renovatebot_schedule_expression != "" ? 1 : 0
  rule      = aws_cloudwatch_event_rule.renovatebot_schedule[0].name
  arn       = aws_ecs_cluster.main.arn
  role_arn  = aws_iam_role.ecs_task_execution.arn
  ecs_target {
    task_count          = 1
    task_definition_arn = aws_ecs_task_definition.renovatebot.arn
    launch_type         = "FARGATE"
    network_configuration {
      subnets         = aws_subnet.private[*].id
      security_groups = [aws_security_group.ecs_tasks.id]
      assign_public_ip = false
    }
  }
}

output "vpc_id" {
  value = aws_vpc.main.id
}

output "public_subnet_ids" {
  value = aws_subnet.public[*].id
}

output "private_subnet_ids" {
  value = aws_subnet.private[*].id
}

output "ecs_cluster_id" {
  value = aws_ecs_cluster.main.id
}

output "ecs_service_name" {
  value = length(aws_ecs_service.renovatebot) > 0 ? aws_ecs_service.renovatebot[0].name : null
}

output "cloudwatch_log_group_name" {
  value = aws_cloudwatch_log_group.renovatebot.name
}
