plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

' Deployment diagram for MLflow-based architecture
node "Nginx\n(Reverse Proxy)" as nginx {
}

node "Supervisor\n(Process Manager)" as supervisor {
}

node "MLflow Tracking Server\n(Python, MLflow 2.4.1)" as mlflow {
}

database "PostgreSQL\n(Database)" as postgres

cloud "AWS S3\n(Artifact Storage)" as s3

nginx --> supervisor : Forwards requests
supervisor --> mlflow : Manages process
mlflow --> postgres : Stores metadata
mlflow --> s3 : Stores artifacts

@enduml
