plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Docker Container\n(Shell, rclone, mysql-client)" as DockerContainer {
  artifact "entrypoint.sh" as Entrypoint
}

database "MySQL Database" as MySQLDB

node "FTP Server" as FTPServer

DockerContainer --> MySQLDB : DB connection (mysql-client)
DockerContainer --> FTPServer : File transfer (rclone)

@enduml
