@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Static Web Hosting\n(AWS S3, Netlify, Vercel, etc.)" as StaticHost {
  artifact "Web App Bundle\n(JavaScript, HTML, CSS)\n[Parcel, TailwindCSS, PostCSS]" as WebApp
}

actor "User" as user

user --|> StaticHost : "HTTP/HTTPS"

@enduml
