plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Developer Workstation" as dev {
  artifact "Source Code\n(HTML, CSS, JS)" as src
}

node "GitLab CI/CD" as ci {
  artifact ".gitlab-ci.yml" as cicd
}

node "Docker Hub" as dockerhub {
  artifact "nginx:latest\nDocker Image" as nginximg
}

node "AWS EC2 Instance" as ec2 {
  node "Nginx Web Server\n(Docker Container)" as nginx {
    artifact "Static Web App\n(Portfolio)" as webapp
  }
}

dev -down-> ci : "Pushes code\nTriggers pipeline"
ci -down-> dockerhub : "Pulls nginx:latest\nBuilds Docker image"
ci -down-> ec2 : "Deploys Docker container"
dockerhub -down-> ec2 : "nginx:latest image"
ec2 -down-> nginx : "Runs Docker container"
nginx -down-> webapp : "Serves static files"
@enduml
