terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = ">= 4.0"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "project_name" {
  description = "Name prefix for all resources."
  type        = string
  default     = "etl-pipeline"
}

variable "s3_bucket_name" {
  description = "Name of the S3 bucket for ETL data."
  type        = string
}

variable "lambda_timeout" {
  description = "Timeout (in seconds) for the Lambda function."
  type        = number
  default     = 900
}

variable "lambda_memory_size" {
  description = "Memory size (in MB) for the Lambda function."
  type        = number
  default     = 1024
}

variable "schedule_expression" {
  description = "Schedule expression for ETL Lambda (e.g., cron or rate)."
  type        = string
  default     = "rate(1 day)"
}

resource "aws_s3_bucket" "etl" {
  bucket = var.s3_bucket_name
  force_destroy = true

  tags = {
    Name        = "${var.project_name}-s3"
    Environment = "dev"
  }
}

resource "aws_s3_bucket_public_access_block" "etl" {
  bucket = aws_s3_bucket.etl.id

  block_public_acls   = true
  block_public_policy = true
  ignore_public_acls  = true
  restrict_public_buckets = true
}

resource "aws_vpc" "etl" {
  cidr_block = "10.0.0.0/16"
  enable_dns_support   = true
  enable_dns_hostnames = true
  tags = {
    Name = "${var.project_name}-vpc"
  }
}

resource "aws_subnet" "private_a" {
  vpc_id            = aws_vpc.etl.id
  cidr_block        = "10.0.1.0/24"
  availability_zone = "${var.aws_region}a"
  tags = {
    Name = "${var.project_name}-subnet-a"
  }
}

resource "aws_subnet" "private_b" {
  vpc_id            = aws_vpc.etl.id
  cidr_block        = "10.0.2.0/24"
  availability_zone = "${var.aws_region}b"
  tags = {
    Name = "${var.project_name}-subnet-b"
  }
}

resource "aws_security_group" "lambda" {
  name        = "${var.project_name}-lambda-sg"
  description = "Security group for Lambda"
  vpc_id      = aws_vpc.etl.id

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "${var.project_name}-lambda-sg"
  }
}

data "aws_iam_policy_document" "lambda_assume_role" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["lambda.amazonaws.com"]
    }
  }
}

resource "aws_iam_role" "lambda" {
  name               = "${var.project_name}-lambda-role"
  assume_role_policy = data.aws_iam_policy_document.lambda_assume_role.json
}

resource "aws_iam_policy" "lambda_s3" {
  name        = "${var.project_name}-lambda-s3-policy"
  description = "Allow Lambda to access S3 bucket"
  policy      = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect = "Allow"
        Action = [
          "s3:GetObject",
          "s3:PutObject",
          "s3:ListBucket"
        ]
        Resource = [
          aws_s3_bucket.etl.arn,
          "${aws_s3_bucket.etl.arn}/*"
        ]
      },
      {
        Effect = "Allow"
        Action = [
          "logs:CreateLogGroup",
          "logs:CreateLogStream",
          "logs:PutLogEvents"
        ]
        Resource = "arn:aws:logs:*:*:*"
      }
    ]
  })
}

resource "aws_iam_role_policy_attachment" "lambda_s3" {
  role       = aws_iam_role.lambda.name
  policy_arn = aws_iam_policy.lambda_s3.arn
}

resource "aws_lambda_function" "etl" {
  function_name = "${var.project_name}-etl"
  role          = aws_iam_role.lambda.arn
  handler       = "main.lambda_handler"
  runtime       = "python3.11"
  timeout       = var.lambda_timeout
  memory_size   = var.lambda_memory_size
  filename      = "lambda_package.zip" # You must build and upload this artifact
  source_code_hash = filebase64sha256("lambda_package.zip")

  vpc_config {
    subnet_ids         = [aws_subnet.private_a.id, aws_subnet.private_b.id]
    security_group_ids = [aws_security_group.lambda.id]
  }

  environment {
    variables = {
      S3_BUCKET = aws_s3_bucket.etl.bucket
    }
  }

  tags = {
    Name = "${var.project_name}-etl-lambda"
  }
}

resource "aws_cloudwatch_log_group" "lambda" {
  name              = "/aws/lambda/${aws_lambda_function.etl.function_name}"
  retention_in_days = 14
}

resource "aws_cloudwatch_event_rule" "etl_schedule" {
  name                = "${var.project_name}-etl-schedule"
  schedule_expression = var.schedule_expression
}

resource "aws_cloudwatch_event_target" "etl_lambda" {
  rule      = aws_cloudwatch_event_rule.etl_schedule.name
  target_id = "lambda-etl"
  arn       = aws_lambda_function.etl.arn
}

resource "aws_lambda_permission" "allow_cloudwatch" {
  statement_id  = "AllowExecutionFromCloudWatch"
  action        = "lambda:InvokeFunction"
  function_name = aws_lambda_function.etl.function_name
  principal     = "events.amazonaws.com"
  source_arn    = aws_cloudwatch_event_rule.etl_schedule.arn
}

output "s3_bucket_name" {
  value = aws_s3_bucket.etl.bucket
}

output "lambda_function_name" {
  value = aws_lambda_function.etl.function_name
}

output "lambda_invoke_arn" {
  value = aws_lambda_function.etl.invoke_arn
}

output "cloudwatch_log_group" {
  value = aws_cloudwatch_log_group.lambda.name
}
