plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Developer Workstation" as dev {
  artifact "install.sh\n(Shell)" as install
  artifact "docker-compose.yml\n(Docker Compose)" as compose
}

node "Docker Host" as docker {
  node "Localstack\n(AWS Emulator)" as localstack
  node "DynamoDB Admin\n(DynamoDB UI)" as dynamodbadmin
}

node "Infrastructure as Code" as iac {
  artifact "Terraform" as terraform
}

dev -down-> install : installs
install -down-> docker : installs Docker
install -down-> compose : installs Docker Compose
compose -down-> docker : launches containers

docker -down-> localstack : runs
localstack -right-> dynamodbadmin : exposes DynamoDB

dev -right-> iac : manages infra
terraform -down-> localstack : provisions resources
@enduml
