plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Developer Workstation" as dev {
  artifact "install.sh\n(Shell)" as install
}

node "Docker Host" as dockerhost {
  artifact "Docker Engine" as docker
  artifact "Docker Compose" as compose
}

node "Localstack Container" as localstack {
  artifact "Localstack\n(local AWS stack)" as ls
}

node "DynamoDB Admin Container" as dynamodbadmin {
  artifact "DynamoDB Admin\n(UI)" as ddbadmin
}

' Relationships
install --> docker : installs
install --> compose : installs
compose --> ls : orchestrates
compose --> ddbadmin : orchestrates
docker --> localstack : runs container
localstack --> dynamodbadmin : exposes DynamoDB endpoint

node "Local DynamoDB (Localstack)" as dynamodb
ls --> dynamodb : emulates

dev --> install : executes

ddbadmin --> dynamodb : manages
@enduml
