files.content:
terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = ">= 5.0"
    }
    kubernetes = {
      source  = "hashicorp/kubernetes"
      version = ">= 2.20.0"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
  token                  = data.aws_eks_cluster_auth.cluster.token
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-west-2"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDRs."
  type        = list(string)
  default     = ["10.0.1.0/24", "10.0.2.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDRs."
  type        = list(string)
  default     = ["10.0.101.0/24", "10.0.102.0/24"]
}

variable "db_username" {
  description = "Username for the RDS PostgreSQL instance."
  type        = string
  default     = "postgres"
}

variable "db_password" {
  description = "Password for the RDS PostgreSQL instance."
  type        = string
  sensitive   = true
}

variable "db_name" {
  description = "Database name for the RDS PostgreSQL instance."
  type        = string
  default     = "appdb"
}

variable "eks_node_instance_type" {
  description = "EC2 instance type for EKS worker nodes."
  type        = string
  default     = "t3.medium"
}

variable "eks_node_desired_capacity" {
  description = "Desired number of worker nodes in EKS node group."
  type        = number
  default     = 2
}

variable "eks_node_max_capacity" {
  description = "Maximum number of worker nodes in EKS node group."
  type        = number
  default     = 3
}

variable "eks_node_min_capacity" {
  description = "Minimum number of worker nodes in EKS node group."
  type        = number
  default     = 1
}

variable "api_service_name" {
  description = "Name of the API Kubernetes service."
  type        = string
  default     = "api-service"
}

variable "container_image" {
  description = "Container image for the API service."
  type        = string
}

module "eks" {
  source          = "terraform-aws-modules/eks/aws"
  version         = "21.10.1"

  name            = "app-eks-cluster"
  kubernetes_version = "1.29"
  vpc_id          = module.vpc.vpc_id
  subnet_ids      = module.vpc.private_subnets

  eks_managed_node_groups = {
    default = {
      desired_size = var.eks_node_desired_capacity
      max_size     = var.eks_node_max_capacity
      min_size     = var.eks_node_min_capacity
      instance_types = [var.eks_node_instance_type]
      subnet_ids     = module.vpc.private_subnets
    }
  }

  tags = {
    Terraform   = "true"
    Environment = "dev"
  }
}

data "aws_eks_cluster" "cluster" {
  name = module.eks.cluster_name
}

data "aws_eks_cluster_auth" "cluster" {
  name = module.eks.cluster_name
}

resource "aws_db_subnet_group" "main" {
  name       = "app-db-subnet-group"
  subnet_ids = module.vpc.private_subnets

  tags = {
    Name = "app-db-subnet-group"
  }
}

resource "aws_security_group" "rds" {
  name        = "rds-sg"
  description = "Allow EKS nodes to access RDS"
  vpc_id      = module.vpc.vpc_id

  ingress {
    from_port   = 5432
    to_port     = 5432
    protocol    = "tcp"
    security_groups = [module.eks.node_security_group_id]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "rds-sg"
  }
}

resource "aws_db_instance" "postgres" {
  identifier              = "app-postgres-db"
  engine                  = "postgres"
  engine_version          = "15.4"
  instance_class          = "db.t3.micro"
  allocated_storage       = 20
  max_allocated_storage   = 100
  db_name                 = var.db_name
  username                = var.db_username
  password                = var.db_password
  db_subnet_group_name    = aws_db_subnet_group.main.name
  vpc_security_group_ids  = [aws_security_group.rds.id]
  multi_az                = true
  storage_encrypted       = true
  skip_final_snapshot     = true
  publicly_accessible     = false

  tags = {
    Name = "app-postgres-db"
  }
}

resource "aws_ecr_repository" "api_service" {
  name = var.api_service_name

  image_scanning_configuration {
    scan_on_push = true
  }

  tags = {
    Name = var.api_service_name
  }
}

output "vpc_id" {
  value = module.vpc.vpc_id
}

output "eks_cluster_name" {
  value = module.eks.cluster_name
}

output "eks_cluster_endpoint" {
  value = module.eks.cluster_endpoint
}

output "ecr_repository_url" {
  value = aws_ecr_repository.api_service.repository_url
}

output "rds_endpoint" {
  value = aws_db_instance.postgres.endpoint
}

resource "kubernetes_namespace" "app" {
  metadata {
    name = "app"
  }
}

resource "kubernetes_secret" "db" {
  metadata {
    name      = "db-secret"
    namespace = kubernetes_namespace.app.metadata[0].name
  }
  data = {
    DB_USER     = var.db_username
    DB_PASSWORD = var.db_password
    DB_NAME     = var.db_name
    DB_HOST     = aws_db_instance.postgres.address
    DB_PORT     = "5432"
  }
}

resource "kubernetes_config_map" "api_config" {
  metadata {
    name      = "api-config"
    namespace = kubernetes_namespace.app.metadata[0].name
  }
  data = {
    NODE_ENV = "production"
  }
}

resource "kubernetes_deployment" "api" {
  metadata {
    name      = var.api_service_name
    namespace = kubernetes_namespace.app.metadata[0].name
    labels = {
      app = var.api_service_name
    }
  }
  spec {
    replicas = 2
    selector {
      match_labels = {
        app = var.api_service_name
      }
    }
    template {
      metadata {
        labels = {
          app = var.api_service_name
        }
      }
      spec {
        container {
          name  = var.api_service_name
          image = var.container_image
          port {
            container_port = 3000
          }
          env_from {
            config_map_ref {
              name = kubernetes_config_map.api_config.metadata[0].name
            }
          }
          env_from {
            secret_ref {
              name = kubernetes_secret.db.metadata[0].name
            }
          }
        }
      }
    }
  }
}

resource "kubernetes_service" "api" {
  metadata {
    name      = var.api_service_name
    namespace = kubernetes_namespace.app.metadata[0].name
  }
  spec {
    selector = {
      app = var.api_service_name
    }
    port {
      port        = 80
      target_port = 3000
    }
    type = "LoadBalancer"
  }
}
