plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Developer Workstation" as dev {
  artifact "docker-compose.yml" as compose
}

node "Docker Host" as docker {
  node "LocalStack Container" as localstack <<AWS Cloud Emulator>> {
    artifact "localstack/localstack" as image
  }
}

dev -down-> compose
compose -down-> docker

docker -down-> localstack
localstack -down-> image

@enduml
