terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "6.28.0"
    }
  }
  required_version = ">= 1.3.0"
}

provider "aws" {
  region = var.aws_region
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDR blocks."
  type        = list(string)
  default     = ["10.0.1.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDR blocks."
  type        = list(string)
  default     = ["10.0.2.0/24"]
}

variable "s3_bucket_name" {
  description = "Name for the S3 bucket."
  type        = string
  default     = "nlp-pipeline-data-bucket"
}

variable "sqs_queue_name" {
  description = "Name for the SQS queue."
  type        = string
  default     = "nlp-pipeline-queue"
}

output "vpc_id" {
  value = aws_vpc.main.id
}

output "public_subnet_ids" {
  value = aws_subnet.public[*].id
}

output "private_subnet_ids" {
  value = aws_subnet.private[*].id
}

output "s3_bucket_arn" {
  value = aws_s3_bucket.data.arn
}

output "sqs_queue_url" {
  value = aws_sqs_queue.pipeline.url
}

output "step_function_arn" {
  value = aws_sfn_state_machine.pipeline.arn
}

output "eventbridge_bus_arn" {
  value = aws_cloudwatch_event_bus.pipeline.arn
}

resource "aws_vpc" "main" {
  cidr_block = var.vpc_cidr
  enable_dns_support   = true
  enable_dns_hostnames = true
  tags = { Name = "nlp-pipeline-vpc" }
}

resource "aws_subnet" "public" {
  count                   = length(var.public_subnet_cidrs)
  vpc_id                  = aws_vpc.main.id
  cidr_block              = var.public_subnet_cidrs[count.index]
  map_public_ip_on_launch = true
  availability_zone       = data.aws_availability_zones.available.names[count.index]
  tags = { Name = "nlp-pipeline-public-subnet-${count.index}" }
}

resource "aws_subnet" "private" {
  count             = length(var.private_subnet_cidrs)
  vpc_id            = aws_vpc.main.id
  cidr_block        = var.private_subnet_cidrs[count.index]
  availability_zone = data.aws_availability_zones.available.names[count.index]
  tags = { Name = "nlp-pipeline-private-subnet-${count.index}" }
}

data "aws_availability_zones" "available" {}

resource "aws_internet_gateway" "gw" {
  vpc_id = aws_vpc.main.id
  tags = { Name = "nlp-pipeline-igw" }
}

resource "aws_route_table" "public" {
  vpc_id = aws_vpc.main.id
  tags = { Name = "nlp-pipeline-public-rt" }
}

resource "aws_route" "public_internet_access" {
  route_table_id         = aws_route_table.public.id
  destination_cidr_block = "0.0.0.0/0"
  gateway_id             = aws_internet_gateway.gw.id
}

resource "aws_route_table_association" "public" {
  count          = length(var.public_subnet_cidrs)
  subnet_id      = aws_subnet.public[count.index].id
  route_table_id = aws_route_table.public.id
}

resource "aws_nat_gateway" "gw" {
  allocation_id = aws_eip.nat.id
  subnet_id     = aws_subnet.public[0].id
  depends_on    = [aws_internet_gateway.gw]
  tags = { Name = "nlp-pipeline-nat-gw" }
}

resource "aws_eip" "nat" {
  domain = "vpc"
}

resource "aws_route_table" "private" {
  vpc_id = aws_vpc.main.id
  tags = { Name = "nlp-pipeline-private-rt" }
}

resource "aws_route" "private_nat_access" {
  route_table_id         = aws_route_table.private.id
  destination_cidr_block = "0.0.0.0/0"
  nat_gateway_id         = aws_nat_gateway.gw.id
}

resource "aws_route_table_association" "private" {
  count          = length(var.private_subnet_cidrs)
  subnet_id      = aws_subnet.private[count.index].id
  route_table_id = aws_route_table.private.id
}

resource "aws_security_group" "lambda" {
  name        = "nlp-pipeline-lambda-sg"
  description = "Allow Lambda access to VPC resources"
  vpc_id      = aws_vpc.main.id
  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
  ingress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = [aws_vpc.main.cidr_block]
  }
  tags = { Name = "nlp-pipeline-lambda-sg" }
}

resource "aws_s3_bucket" "data" {
  bucket = var.s3_bucket_name
  force_destroy = true
  tags = { Name = "nlp-pipeline-data-bucket" }
}

resource "aws_sqs_queue" "pipeline" {
  name = var.sqs_queue_name
  tags = { Name = "nlp-pipeline-queue" }
}

resource "aws_iam_role" "lambda_exec" {
  name = "nlp-pipeline-lambda-exec"
  assume_role_policy = data.aws_iam_policy_document.lambda_assume_role.json
}

data "aws_iam_policy_document" "lambda_assume_role" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["lambda.amazonaws.com"]
    }
  }
}

resource "aws_iam_role_policy_attachment" "lambda_basic" {
  role       = aws_iam_role.lambda_exec.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole"
}

resource "aws_iam_role_policy_attachment" "lambda_vpc" {
  role       = aws_iam_role.lambda_exec.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaVPCAccessExecutionRole"
}

resource "aws_iam_role_policy" "lambda_custom" {
  name = "nlp-pipeline-lambda-custom"
  role = aws_iam_role.lambda_exec.id
  policy = data.aws_iam_policy_document.lambda_custom.json
}

data "aws_iam_policy_document" "lambda_custom" {
  statement {
    actions = [
      "s3:GetObject",
      "s3:PutObject",
      "s3:ListBucket"
    ]
    resources = [
      aws_s3_bucket.data.arn,
      "${aws_s3_bucket.data.arn}/*"
    ]
  }
  statement {
    actions = [
      "sqs:SendMessage",
      "sqs:ReceiveMessage",
      "sqs:DeleteMessage",
      "sqs:GetQueueAttributes"
    ]
    resources = [aws_sqs_queue.pipeline.arn]
  }
  statement {
    actions = [
      "comprehend:DetectEntities",
      "comprehend:DetectSentiment",
      "comprehend:DetectKeyPhrases",
      "comprehend:DetectSyntax"
    ]
    resources = ["*"]
  }
  statement {
    actions = [
      "glue:StartJobRun",
      "glue:GetJobRun",
      "glue:GetJobRuns",
      "glue:GetJob",
      "glue:BatchGetJobs"
    ]
    resources = ["*"]
  }
  statement {
    actions = [
      "events:PutEvents"
    ]
    resources = ["*"]
  }
}

resource "aws_cloudwatch_log_group" "lambda" {
  name              = "/aws/lambda/nlp-pipeline"
  retention_in_days = 14
}

resource "aws_sfn_state_machine" "pipeline" {
  name     = "nlp-pipeline-step-function"
  role_arn = aws_iam_role.lambda_exec.arn
  definition = <<EOF
{
  "Comment": "NLP Pipeline State Machine",
  "StartAt": "ProcessSQSMessage",
  "States": {
    "ProcessSQSMessage": {
      "Type": "Task",
      "Resource": "arn:aws:states:::lambda:invoke",
      "Parameters": {
        "FunctionName": "arn:aws:lambda:${var.aws_region}:123456789012:function:nlp-pipeline-processor"
      },
      "End": true
    }
  }
}
EOF
}

resource "aws_cloudwatch_event_bus" "pipeline" {
  name = "nlp-pipeline-bus"
}

resource "aws_cloudwatch_event_rule" "s3_object_created" {
  name        = "nlp-pipeline-s3-object-created"
  event_bus_name = aws_cloudwatch_event_bus.pipeline.name
  event_pattern = <<EOF
{
  "source": ["aws.s3"],
  "detail-type": ["Object Created"],
  "resources": ["${aws_s3_bucket.data.arn}"]
}
EOF
}

resource "aws_cloudwatch_event_target" "step_function" {
  rule      = aws_cloudwatch_event_rule.s3_object_created.name
  arn       = aws_sfn_state_machine.pipeline.arn
  event_bus_name = aws_cloudwatch_event_bus.pipeline.name
}

resource "aws_glue_catalog_database" "nlp" {
  name = "nlp_pipeline_db"
}

resource "aws_glue_job" "nlp_etl" {
  name     = "nlp-pipeline-etl-job"
  role_arn = aws_iam_role.lambda_exec.arn
  command {
    name            = "glueetl"
    script_location = "s3://${aws_s3_bucket.data.bucket}/scripts/etl_script.py"
    python_version  = "3"
  }
  max_capacity = 2
  glue_version = "4.0"
}
