terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = ">= 5.0"
    }
    kubernetes = {
      source  = "hashicorp/kubernetes"
      version = ">= 2.20"
    }
    helm = {
      source  = "hashicorp/helm"
      version = ">= 2.10"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
  token                  = data.aws_eks_cluster_auth.cluster.token
}

provider "helm" {
  kubernetes = {
    host                   = module.eks.cluster_endpoint
    cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
    token                  = data.aws_eks_cluster_auth.cluster.token
  }
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-west-2"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDRs."
  type        = list(string)
  default     = ["10.0.1.0/24", "10.0.2.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDRs."
  type        = list(string)
  default     = ["10.0.101.0/24", "10.0.102.0/24"]
}

variable "eks_cluster_name" {
  description = "Name for the EKS cluster."
  type        = string
  default     = "go-fuego-eks"
}

variable "node_group_instance_types" {
  description = "EC2 instance types for EKS node group."
  type        = list(string)
  default     = ["t3.medium"]
}

variable "desired_capacity" {
  description = "Desired number of worker nodes."
  type        = number
  default     = 2
}

variable "max_capacity" {
  description = "Maximum number of worker nodes."
  type        = number
  default     = 3
}

variable "min_capacity" {
  description = "Minimum number of worker nodes."
  type        = number
  default     = 1
}

variable "api_image" {
  description = "Docker image for the Go API service."
  type        = string
}

module "vpc" {
  source  = "terraform-aws-modules/vpc/aws"
  version = "5.1.0"

  name = "go-fuego-vpc"
  cidr = var.vpc_cidr

  azs             = [for i in range(length(var.public_subnet_cidrs)) : "${var.aws_region}${substr("abc", i, 1)}"]
  public_subnets  = var.public_subnet_cidrs
  private_subnets = var.private_subnet_cidrs

  enable_nat_gateway   = true
  single_nat_gateway   = true
  enable_dns_hostnames = true
  enable_dns_support   = true

  tags = {
    Name = "go-fuego-vpc"
  }
}

module "eks" {
  source          = "terraform-aws-modules/eks/aws"
  version         = "20.8.4"

  cluster_name    = var.eks_cluster_name
  cluster_version = "1.29"
  subnet_ids      = module.vpc.private_subnets
  vpc_id          = module.vpc.vpc_id

  eks_managed_node_group_defaults = {
    instance_types = var.node_group_instance_types
  }

  eks_managed_node_groups = {
    default = {
      min_size     = var.min_capacity
      max_size     = var.max_capacity
      desired_size = var.desired_capacity
    }
  }

  tags = {
    Name = var.eks_cluster_name
  }
}

data "aws_eks_cluster" "cluster" {
  name = module.eks.cluster_name
}

data "aws_eks_cluster_auth" "cluster" {
  name = module.eks.cluster_name
}

resource "aws_ecr_repository" "api" {
  name = "go-fuego-api"
  image_scanning_configuration {
    scan_on_push = true
  }
  tags = {
    Name = "go-fuego-api"
  }
}

resource "kubernetes_namespace" "istio_system" {
  metadata {
    name = "istio-system"
  }
}

resource "helm_release" "istio_base" {
  name       = "istio-base"
  repository = "https://istio-release.storage.googleapis.com/charts"
  chart      = "base"
  namespace  = kubernetes_namespace.istio_system.metadata[0].name
}

resource "helm_release" "istiod" {
  name       = "istiod"
  repository = "https://istio-release.storage.googleapis.com/charts"
  chart      = "istiod"
  namespace  = kubernetes_namespace.istio_system.metadata[0].name
  depends_on = [helm_release.istio_base]
}

resource "helm_release" "istio_ingress" {
  name       = "istio-ingress"
  repository = "https://istio-release.storage.googleapis.com/charts"
  chart      = "gateway"
  namespace  = kubernetes_namespace.istio_system.metadata[0].name
  depends_on = [helm_release.istiod]
}

resource "kubernetes_namespace" "otel" {
  metadata {
    name = "otel"
  }
}

resource "helm_release" "opentelemetry_collector" {
  name       = "otel-collector"
  repository = "https://open-telemetry.github.io/opentelemetry-helm-charts"
  chart      = "opentelemetry-collector"
  namespace  = kubernetes_namespace.otel.metadata[0].name
  version    = "0.73.1"
}

resource "kubernetes_namespace" "app" {
  metadata {
    name = "app"
  }
}

resource "kubernetes_deployment" "api" {
  metadata {
    name      = "go-fuego-api"
    namespace = kubernetes_namespace.app.metadata[0].name
    labels = {
      app = "go-fuego-api"
    }
  }
  spec {
    replicas = 2
    selector {
      match_labels = {
        app = "go-fuego-api"
      }
    }
    template {
      metadata {
        labels = {
          app = "go-fuego-api"
        }
      }
      spec {
        container {
          name  = "go-fuego-api"
          image = var.api_image
          port {
            container_port = 8080
          }
          env {
            name  = "OTEL_EXPORTER_OTLP_ENDPOINT"
            value = "http://otel-collector.otel:4317"
          }
        }
      }
    }
  }
}

resource "kubernetes_service" "api" {
  metadata {
    name      = "go-fuego-api"
    namespace = kubernetes_namespace.app.metadata[0].name
    labels = {
      app = "go-fuego-api"
    }
  }
  spec {
    selector = {
      app = "go-fuego-api"
    }
    port {
      port        = 80
      target_port = 8080
    }
  }
}

resource "kubernetes_manifest" "istio_virtualservice" {
  manifest = {
    "apiVersion" = "networking.istio.io/v1beta1"
    "kind" = "VirtualService"
    "metadata" = {
      "name" = "go-fuego-api"
      "namespace" = kubernetes_namespace.app.metadata[0].name
    }
    "spec" = {
      "hosts" = ["*"]
      "gateways" = ["istio-ingress/istio-ingressgateway"]
      "http" = [{
        "route" = [{
          "destination" = {
            "host" = "go-fuego-api.app.svc.cluster.local"
            "port" = { "number" = 80 }
          }
        }]
      }]
    }
  }
}
