plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Developer Machine" as dev {
  artifact "Terraform Scripts (HCL)" as tf
  artifact "Shell Scripts" as sh
}

node "Docker Host" as docker {
  node "LocalStack (AWS Emulation)" as localstack {
    node "API Gateway (Emulated)" as apigw
    node "Lambda (Java)" as lambda_java
    node "Lambda (Node.js)" as lambda_node
  }
}

artifact "product-lambda.jar" as jar
artifact "lambda.zip" as zip

' Relationships

dev -down-> docker : "docker-compose up"
tf -down-> localstack : "Provision AWS Resources"
sh -down-> localstack : "Invoke/Manage Resources"
jar -down-> lambda_java : "Deployed as Java Lambda"
zip -down-> lambda_node : "Deployed as Node.js Lambda"
apigw -down-> lambda_java : "Triggers"
apigw -down-> lambda_node : "Triggers"

@enduml
