terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "6.28.0"
    }
    kubernetes = {
      source  = "hashicorp/kubernetes"
      version = "3.0.1"
    }
    helm = {
      source  = "hashicorp/helm"
      version = "3.1.1"
    }
  }
  required_version = ">= 1.3.0"
}

provider "aws" {
  region = var.aws_region
}

provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
  token                  = data.aws_eks_cluster_auth.cluster.token
}

provider "helm" {
  kubernetes = {
    host                   = module.eks.cluster_endpoint
    cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
    token                  = data.aws_eks_cluster_auth.cluster.token
  }
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDRs."
  type        = list(string)
  default     = ["10.0.1.0/24", "10.0.2.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDRs."
  type        = list(string)
  default     = ["10.0.101.0/24", "10.0.102.0/24"]
}

variable "eks_cluster_name" {
  description = "Name for the EKS cluster."
  type        = string
  default     = "flask-eks-cluster"
}

variable "node_group_instance_types" {
  description = "EC2 instance types for EKS node group."
  type        = list(string)
  default     = ["t3.medium"]
}

variable "desired_capacity" {
  description = "Desired number of worker nodes."
  type        = number
  default     = 2
}

variable "max_capacity" {
  description = "Maximum number of worker nodes."
  type        = number
  default     = 3
}

variable "min_capacity" {
  description = "Minimum number of worker nodes."
  type        = number
  default     = 1
}

variable "flask_app_image" {
  description = "Docker image for the Flask app."
  type        = string
}

variable "openweathermap_api_key" {
  description = "API key for OpenWeatherMap."
  type        = string
  sensitive   = true
}

module "eks" {
  source          = "terraform-aws-modules/eks/aws"
  version         = "21.14.0"

  cluster_name    = var.eks_cluster_name
  cluster_version = "1.29"
  vpc_id          = module.vpc.vpc_id
  subnet_ids      = module.vpc.private_subnets

  enable_irsa = true

  eks_managed_node_groups = {
    default = {
      min_size     = var.min_capacity
      max_size     = var.max_capacity
      desired_size = var.desired_capacity
      instance_types = var.node_group_instance_types
    }
  }

  tags = {
    Environment = "production"
    Application = "flask-app"
  }
}

data "aws_eks_cluster" "cluster" {
  name = module.eks.cluster_name
}

data "aws_eks_cluster_auth" "cluster" {
  name = module.eks.cluster_name
}

data "aws_iam_policy_document" "eks_secrets_manager" {
  statement {
    actions   = ["secretsmanager:GetSecretValue"]
    resources = [aws_secretsmanager_secret.openweathermap.arn]
  }
}

resource "aws_iam_policy" "eks_secrets_manager" {
  name        = "eks-secretsmanager-policy"
  description = "Allow EKS pods to access OpenWeatherMap secret"
  policy      = data.aws_iam_policy_document.eks_secrets_manager.json
}

module "irsa_sa" {
  source  = "terraform-aws-modules/iam/aws//modules/iam-assumable-role-with-oidc"
  version = "5.34.0"

  create_role                   = true
  role_name                     = "flask-app-sa-irsa"
  provider_url                  = module.eks.oidc_provider
  oidc_fully_qualified_subjects = ["system:serviceaccount:default:flask-app-sa"]
  role_policy_arns              = [aws_iam_policy.eks_secrets_manager.arn]
}

resource "aws_secretsmanager_secret" "openweathermap" {
  name = "openweathermap-api-key"
}

resource "aws_secretsmanager_secret_version" "openweathermap" {
  secret_id     = aws_secretsmanager_secret.openweathermap.id
  secret_string = var.openweathermap_api_key
}

output "vpc_id" {
  value = module.vpc.vpc_id
}

output "eks_cluster_name" {
  value = module.eks.cluster_name
}

output "eks_cluster_endpoint" {
  value = module.eks.cluster_endpoint
}

output "eks_node_group_role_arn" {
  value = module.eks.eks_managed_node_groups["default"].iam_role_arn
}

output "openweathermap_secret_arn" {
  value = aws_secretsmanager_secret.openweathermap.arn
}

resource "helm_release" "aws_load_balancer_controller" {
  name       = "aws-load-balancer-controller"
  repository = "https://aws.github.io/eks-charts"
  chart      = "aws-load-balancer-controller"
  namespace  = "kube-system"
  version    = "1.7.1"

  set {
    name  = "clusterName"
    value = module.eks.cluster_name
  }
  set {
    name  = "serviceAccount.create"
    value = "false"
  }
  set {
    name  = "serviceAccount.name"
    value = module.eks.aws_load_balancer_controller_service_account_name
  }
  set {
    name  = "region"
    value = var.aws_region
  }
  set {
    name  = "vpcId"
    value = module.vpc.vpc_id
  }
  depends_on = [module.eks]
}

resource "kubernetes_service_account" "flask_app" {
  metadata {
    name      = "flask-app-sa"
    namespace = "default"
    annotations = {
      "eks.amazonaws.com/role-arn" = module.irsa_sa.iam_role_arn
    }
  }
}

resource "kubernetes_deployment" "flask_app" {
  metadata {
    name      = "flask-app"
    namespace = "default"
    labels = {
      app = "flask-app"
    }
  }
  spec {
    replicas = 2
    selector {
      match_labels = {
        app = "flask-app"
      }
    }
    template {
      metadata {
        labels = {
          app = "flask-app"
        }
      }
      spec {
        service_account_name = kubernetes_service_account.flask_app.metadata[0].name
        container {
          name  = "flask-app"
          image = var.flask_app_image
          port {
            container_port = 8080
          }
          env {
            name = "OPENWEATHERMAP_API_KEY"
            value_from {
              secret_key_ref {
                name = "openweathermap-api-key"
                key  = "openweathermap_api_key"
              }
            }
          }
        }
      }
    }
  }
}

resource "kubernetes_service" "flask_app" {
  metadata {
    name      = "flask-app-service"
    namespace = "default"
    labels = {
      app = "flask-app"
    }
  }
  spec {
    selector = {
      app = "flask-app"
    }
    port {
      port        = 80
      target_port = 8080
    }
    type = "NodePort"
  }
}

resource "kubernetes_ingress_v1" "flask_app" {
  metadata {
    name      = "flask-app-ingress"
    namespace = "default"
    annotations = {
      "kubernetes.io/ingress.class" : "alb"
      "alb.ingress.kubernetes.io/scheme" : "internet-facing"
      "alb.ingress.kubernetes.io/target-type" : "ip"
      "alb.ingress.kubernetes.io/listen-ports" = "[{\"HTTP\":80}]"
    }
  }
  spec {
    rule {
      http {
        path {
          path     = "/"
          path_type = "Prefix"
          backend {
            service {
              name = kubernetes_service.flask_app.metadata[0].name
              port {
                number = 80
              }
            }
          }
        }
      }
    }
  }
}
