plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "AWS Cloud" as aws {
  node "Apache Airflow\n(Python, Apache Airflow)" as airflow {
    artifact "DAGs\n(Python)" as dags
  }
  node "AWS Lambda\n(Python, boto3)" as lambda
  node "S3/Data Storage\n(AWS, boto3)" as s3
}

airflow -down-> lambda : Triggers via boto3
lambda -down-> airflow : Triggers DAG via boto3
lambda --> s3 : Reads/Writes Data

@enduml
