terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 6.27.0"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDR blocks."
  type        = list(string)
  default     = ["10.0.1.0/24", "10.0.2.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDR blocks."
  type        = list(string)
  default     = ["10.0.101.0/24", "10.0.102.0/24"]
}

variable "dag_s3_bucket_name" {
  description = "Name for the S3 bucket to store Airflow DAGs."
  type        = string
}

variable "data_s3_bucket_name" {
  description = "Name for the S3 bucket to store data."
  type        = string
}

variable "lambda_function_name" {
  description = "Name for the Lambda function."
  type        = string
}

variable "lambda_s3_key" {
  description = "S3 key for the Lambda deployment package."
  type        = string
}

variable "lambda_s3_bucket" {
  description = "S3 bucket for the Lambda deployment package."
  type        = string
}

output "vpc_id" {
  description = "The ID of the VPC."
  value       = aws_vpc.main.id
}

output "public_subnet_ids" {
  description = "IDs of the public subnets."
  value       = aws_subnet.public[*].id
}

output "private_subnet_ids" {
  description = "IDs of the private subnets."
  value       = aws_subnet.private[*].id
}

output "dag_s3_bucket_arn" {
  description = "ARN of the S3 bucket for Airflow DAGs."
  value       = aws_s3_bucket.dag.arn
}

output "data_s3_bucket_arn" {
  description = "ARN of the S3 bucket for data."
  value       = aws_s3_bucket.data.arn
}

output "lambda_function_arn" {
  description = "ARN of the Lambda function."
  value       = aws_lambda_function.trigger_dag.arn
}

output "mwaa_environment_arn" {
  description = "ARN of the MWAA environment."
  value       = aws_mwaa_environment.main.arn
}

resource "aws_vpc" "main" {
  cidr_block           = var.vpc_cidr
  enable_dns_support   = true
  enable_dns_hostnames = true
  tags = {
    Name = "main-vpc"
  }
}

resource "aws_internet_gateway" "main" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "main-igw"
  }
}

resource "aws_subnet" "public" {
  count                   = length(var.public_subnet_cidrs)
  vpc_id                  = aws_vpc.main.id
  cidr_block              = var.public_subnet_cidrs[count.index]
  map_public_ip_on_launch = true
  availability_zone       = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "public-subnet-${count.index + 1}"
  }
}

resource "aws_subnet" "private" {
  count             = length(var.private_subnet_cidrs)
  vpc_id            = aws_vpc.main.id
  cidr_block        = var.private_subnet_cidrs[count.index]
  availability_zone = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "private-subnet-${count.index + 1}"
  }
}

resource "aws_nat_gateway" "main" {
  allocation_id = aws_eip.nat.id
  subnet_id     = aws_subnet.public[0].id
  depends_on    = [aws_internet_gateway.main]
  tags = {
    Name = "main-nat-gw"
  }
}

resource "aws_eip" "nat" {
  domain = "vpc"
}

resource "aws_route_table" "public" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "public-rt"
  }
}

resource "aws_route" "public_internet_access" {
  route_table_id         = aws_route_table.public.id
  destination_cidr_block = "0.0.0.0/0"
  gateway_id             = aws_internet_gateway.main.id
}

resource "aws_route_table_association" "public" {
  count          = length(var.public_subnet_cidrs)
  subnet_id      = aws_subnet.public[count.index].id
  route_table_id = aws_route_table.public.id
}

resource "aws_route_table" "private" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "private-rt"
  }
}

resource "aws_route" "private_nat_gateway" {
  route_table_id         = aws_route_table.private.id
  destination_cidr_block = "0.0.0.0/0"
  nat_gateway_id         = aws_nat_gateway.main.id
}

resource "aws_route_table_association" "private" {
  count          = length(var.private_subnet_cidrs)
  subnet_id      = aws_subnet.private[count.index].id
  route_table_id = aws_route_table.private.id
}

data "aws_availability_zones" "available" {}

resource "aws_s3_bucket" "dag" {
  bucket = var.dag_s3_bucket_name
  force_destroy = true
  tags = {
    Name = "airflow-dags-bucket"
  }
}

resource "aws_s3_bucket_versioning" "dag" {
  bucket = aws_s3_bucket.dag.id
  versioning_configuration {
    status = "Enabled"
  }
}

resource "aws_s3_bucket_server_side_encryption_configuration" "dag" {
  bucket = aws_s3_bucket.dag.id
  rule {
    apply_server_side_encryption_by_default {
      sse_algorithm = "AES256"
    }
  }
}

resource "aws_s3_bucket_public_access_block" "dag" {
  bucket = aws_s3_bucket.dag.id
  block_public_acls   = true
  block_public_policy = true
  ignore_public_acls  = true
  restrict_public_buckets = true
}

resource "aws_s3_bucket" "data" {
  bucket = var.data_s3_bucket_name
  force_destroy = true
  tags = {
    Name = "data-storage-bucket"
  }
}

resource "aws_s3_bucket_versioning" "data" {
  bucket = aws_s3_bucket.data.id
  versioning_configuration {
    status = "Enabled"
  }
}

resource "aws_s3_bucket_server_side_encryption_configuration" "data" {
  bucket = aws_s3_bucket.data.id
  rule {
    apply_server_side_encryption_by_default {
      sse_algorithm = "AES256"
    }
  }
}

resource "aws_s3_bucket_public_access_block" "data" {
  bucket = aws_s3_bucket.data.id
  block_public_acls   = true
  block_public_policy = true
  ignore_public_acls  = true
  restrict_public_buckets = true
}

resource "aws_iam_role" "lambda" {
  name = "lambda-execution-role"
  assume_role_policy = data.aws_iam_policy_document.lambda_assume_role_policy.json
}

data "aws_iam_policy_document" "lambda_assume_role_policy" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["lambda.amazonaws.com"]
    }
  }
}

resource "aws_iam_role_policy" "lambda_policy" {
  name = "lambda-inline-policy"
  role = aws_iam_role.lambda.id
  policy = data.aws_iam_policy_document.lambda_policy.json
}

data "aws_iam_policy_document" "lambda_policy" {
  statement {
    actions = [
      "logs:CreateLogGroup",
      "logs:CreateLogStream",
      "logs:PutLogEvents",
      "s3:GetObject",
      "s3:PutObject",
      "s3:ListBucket"
    ]
    resources = [
      aws_s3_bucket.dag.arn,
      "${aws_s3_bucket.dag.arn}/*",
      aws_s3_bucket.data.arn,
      "${aws_s3_bucket.data.arn}/*"
    ]
  }
  statement {
    actions = [
      "airflow:PublishMetrics",
      "airflow:GetEnvironment",
      "airflow:ListEnvironments"
    ]
    resources = ["*"]
  }
}

resource "aws_iam_role" "mwaa" {
  name = "mwaa-execution-role"
  assume_role_policy = data.aws_iam_policy_document.mwaa_assume_role_policy.json
}

data "aws_iam_policy_document" "mwaa_assume_role_policy" {
  statement {
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["airflow.amazonaws.com"]
    }
  }
}

resource "aws_iam_role_policy" "mwaa_policy" {
  name = "mwaa-inline-policy"
  role = aws_iam_role.mwaa.id
  policy = data.aws_iam_policy_document.mwaa_policy.json
}

data "aws_iam_policy_document" "mwaa_policy" {
  statement {
    actions = [
      "s3:GetObject",
      "s3:ListBucket",
      "logs:CreateLogGroup",
      "logs:CreateLogStream",
      "logs:PutLogEvents",
      "lambda:InvokeFunction"
    ]
    resources = [
      aws_s3_bucket.dag.arn,
      "${aws_s3_bucket.dag.arn}/*",
      aws_s3_bucket.data.arn,
      "${aws_s3_bucket.data.arn}/*",
      aws_lambda_function.trigger_dag.arn
    ]
  }
}

resource "aws_lambda_function" "trigger_dag" {
  function_name = var.lambda_function_name
  s3_bucket     = var.lambda_s3_bucket
  s3_key        = var.lambda_s3_key
  handler       = "index.handler"
  runtime       = "python3.9"
  role          = aws_iam_role.lambda.arn
  timeout       = 60
  memory_size   = 128
  vpc_config {
    subnet_ids         = aws_subnet.private[*].id
    security_group_ids = [aws_security_group.lambda.id]
  }
  environment {
    variables = {
      DAG_BUCKET = aws_s3_bucket.dag.bucket
      DATA_BUCKET = aws_s3_bucket.data.bucket
    }
  }
  depends_on = [aws_iam_role_policy.lambda_policy]
}

resource "aws_security_group" "lambda" {
  name        = "lambda-sg"
  description = "Security group for Lambda functions"
  vpc_id      = aws_vpc.main.id
  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
  ingress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
  tags = {
    Name = "lambda-sg"
  }
}

resource "aws_security_group" "mwaa" {
  name        = "mwaa-sg"
  description = "Security group for MWAA"
  vpc_id      = aws_vpc.main.id
  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
  ingress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
  tags = {
    Name = "mwaa-sg"
  }
}

resource "aws_mwaa_environment" "main" {
  name                       = "main-mwaa-env"
  airflow_version            = "2.6.3"
  environment_class          = "mw1.small"
  execution_role_arn         = aws_iam_role.mwaa.arn
  source_bucket_arn          = aws_s3_bucket.dag.arn
  dag_s3_path                = "/"
  requirements_s3_path       = "requirements.txt"
  network_configuration {
    security_group_ids = [aws_security_group.mwaa.id]
    subnet_ids         = aws_subnet.private[*].id
  }
  logging_configuration {
    dag_processing_logs {
      enabled   = true
      log_level = "INFO"
    }
    scheduler_logs {
      enabled   = true
      log_level = "INFO"
    }
    task_logs {
      enabled   = true
      log_level = "INFO"
    }
    webserver_logs {
      enabled   = true
      log_level = "INFO"
    }
    worker_logs {
      enabled   = true
      log_level = "INFO"
    }
  }
  airflow_configuration_options = {
    "core.load_examples" = "False"
  }
  max_workers = 2
  min_workers = 1
  webserver_access_mode = "PUBLIC_ONLY"
  depends_on = [aws_iam_role_policy.mwaa_policy]
}

resource "aws_cloudwatch_log_group" "lambda" {
  name              = "/aws/lambda/${var.lambda_function_name}"
  retention_in_days = 14
}

resource "aws_cloudwatch_log_group" "mwaa" {
  name              = "/aws/mwaa/main-mwaa-env"
  retention_in_days = 14
}

resource "aws_cloudtrail" "main" {
  name                          = "main-cloudtrail"
  s3_bucket_name                = aws_s3_bucket.data.bucket
  include_global_service_events = true
  is_multi_region_trail         = false
  enable_log_file_validation    = true
  event_selector {
    read_write_type           = "All"
    include_management_events = true
  }
}
