plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Rust Macro Code Generator\n(Rust CLI)" as rust_cli <<Rust>> {
  artifact "main.rs"
}
node "COBOL Source Code" as cobol_code <<COBOL>> {
  artifact "main.cbl"
  artifact "numbers.cbl"
  artifact "working_with_input.cob"
}
node "Script Automation" as scripts <<Shell/PowerShell>> {
  artifact "clean_up.sh"
  artifact "environment.sh"
  artifact "clean_up.ps1"
}

scripts --> rust_cli : "Setup/Cleanup\nEnvironment"
rust_cli --> cobol_code : "Generates/Processes\nCOBOL Source"
@enduml
