files.content:
terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = ">= 5.0"
    }
    kubernetes = {
      source  = "hashicorp/kubernetes"
      version = ">= 2.20"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
  token                  = data.aws_eks_cluster_auth.cluster.token
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-west-2"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDRs."
  type        = list(string)
  default     = ["10.0.1.0/24", "10.0.2.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDRs."
  type        = list(string)
  default     = ["10.0.101.0/24", "10.0.102.0/24"]
}

variable "eks_cluster_name" {
  description = "Name for the EKS cluster."
  type        = string
  default     = "rust-python-eks-cluster"
}

variable "node_group_instance_types" {
  description = "EC2 instance types for EKS node group."
  type        = list(string)
  default     = ["t3.medium"]
}

variable "desired_capacity" {
  description = "Desired number of worker nodes."
  type        = number
  default     = 2
}

variable "max_capacity" {
  description = "Maximum number of worker nodes."
  type        = number
  default     = 3
}

variable "min_capacity" {
  description = "Minimum number of worker nodes."
  type        = number
  default     = 1
}

variable "s3_bucket_name" {
  description = "Name of the S3 bucket for application storage."
  type        = string
  default     = "rust-python-app-bucket"
}

variable "namespace" {
  description = "Kubernetes namespace for the application."
  type        = string
  default     = "rust-python-app"
}

module "eks" {
  source          = "terraform-aws-modules/eks/aws"
  version         = "20.8.4"

  cluster_name    = var.eks_cluster_name
  cluster_version = "1.29"
  vpc_id          = module.vpc.vpc_id
  subnet_ids      = module.vpc.private_subnets

  enable_irsa = true

  eks_managed_node_group_defaults = {
    instance_types = var.node_group_instance_types
  }

  eks_managed_node_groups = {
    default = {
      min_size     = var.min_capacity
      max_size     = var.max_capacity
      desired_size = var.desired_capacity
    }
  }

  tags = {
    Name = var.eks_cluster_name
  }
}

data "aws_eks_cluster" "cluster" {
  name = module.eks.cluster_name
}

data "aws_eks_cluster_auth" "cluster" {
  name = module.eks.cluster_name
}

data "aws_iam_policy_document" "s3_access" {
  statement {
    actions   = ["s3:ListBucket"]
    resources = [aws_s3_bucket.app_bucket.arn]
  }
  statement {
    actions   = ["s3:GetObject", "s3:PutObject", "s3:DeleteObject"]
    resources = ["${aws_s3_bucket.app_bucket.arn}/*"]
  }
}

resource "aws_iam_policy" "s3_access" {
  name        = "rust-python-app-s3-access"
  description = "Allow access to S3 bucket for app"
  policy      = data.aws_iam_policy_document.s3_access.json
}

module "irsa" {
  source  = "terraform-aws-modules/iam/aws//modules/iam-role-for-service-accounts-eks"
  version = "5.34.0"

  role_name             = "rust-python-app-irsa"
  attach_custom_policies = true
  custom_policy_arns    = [aws_iam_policy.s3_access.arn]
  oidc_providers        = [
    {
      provider_arn               = module.eks.oidc_provider_arn
      namespace_service_accounts = ["${var.namespace}:rust-python-app-sa"]
    }
  ]
}

resource "aws_s3_bucket" "app_bucket" {
  bucket = var.s3_bucket_name
  force_destroy = true

  tags = {
    Name = var.s3_bucket_name
  }
}

resource "aws_s3_bucket_public_access_block" "app_bucket" {
  bucket = aws_s3_bucket.app_bucket.id

  block_public_acls   = true
  block_public_policy = true
  ignore_public_acls  = true
  restrict_public_buckets = true
}

resource "kubernetes_namespace" "app" {
  metadata {
    name = var.namespace
  }
}

resource "kubernetes_service_account" "app_sa" {
  metadata {
    name      = "rust-python-app-sa"
    namespace = kubernetes_namespace.app.metadata[0].name
    annotations = {
      "eks.amazonaws.com/role-arn" = module.irsa.iam_role_arn
    }
  }
}

output "vpc_id" {
  value = module.vpc.vpc_id
}

output "eks_cluster_name" {
  value = module.eks.cluster_name
}

output "eks_cluster_endpoint" {
  value = module.eks.cluster_endpoint
}

output "eks_node_group_role_arn" {
  value = module.eks.node_group_iam_role_arn
}

output "s3_bucket_name" {
  value = aws_s3_bucket.app_bucket.bucket
}

output "irsa_role_arn" {
  value = module.irsa.iam_role_arn
}
