plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "AWS Cloud" {
  node "AWS Lambda Function\n(Java, AWS Lambda SDK)" as lambdaFn
}

artifact "pom.xml" as pom

node "Environment Variables" as envVars {
  [bot_token]
  [chat_id]
  [half_month_message]
  [end_month_message]
}

actor "Telegram Bot API" as telegram

pom -down-> lambdaFn : "Builds & Deploys"

envVars -down-> lambdaFn : "Configures"
lambdaFn -right-> telegram : "Sends/Receives HTTP Requests"

@enduml
