plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

' AWS Cloud
node "AWS Cloud" as aws {
  node "EKS Cluster" as eks {
    node "Kubernetes Deployment\n(dinosaur-deployment)" as k8s_deploy {
      node "Docker Container\n(Nginx, Static Site)" as nginx {
        artifact "Web App\n(JavaScript, HTML, CSS)" as webapp
      }
    }
    node "Kubernetes Service\n(dinosaur-service, LoadBalancer, AWS ALB)" as k8s_svc
  }
  node "AWS Infrastructure\n(Terraform managed)" as tf
}

' CI/CD Pipelines
node "CI/CD" as cicd {
  node "Jenkins Pipeline" as jenkins
  node "GitHub Actions Pipeline" as gha
}

' Static Web Application (Jump Game)
node "Static Web App\n(Jump Game)" as jumpgame {
  artifact "Jump Game\n(JavaScript, HTML, CSS)" as jumpgame_artifact
}

' Relationships
webapp -down-> nginx : "Served by"
nginx -down-> k8s_deploy : "Runs in"
k8s_deploy -down-> k8s_svc : "Exposed by"
k8s_svc -down-> eks : "Deployed on"
eks -down-> aws : "Hosted in"
tf -down-> aws : "Manages"
jenkins -right-> k8s_deploy : "Deploys to"
gha -right-> k8s_deploy : "Deploys to"
jumpgame_artifact -down-> jumpgame : "Served by"
jumpgame -right-> aws : "Hosted in S3/Static Hosting (inferred)"
@enduml
