terraform {
  required_version = ">= 1.3.0"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = ">= 4.0"
    }
    kubernetes = {
      source  = "hashicorp/kubernetes"
      version = ">= 2.11.0"
    }
  }
}

provider "aws" {
  region = var.aws_region
}

provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
  token                  = data.aws_eks_cluster_auth.cluster.token
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-west-2"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDRs."
  type        = list(string)
  default     = ["10.0.1.0/24", "10.0.2.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDRs."
  type        = list(string)
  default     = ["10.0.101.0/24", "10.0.102.0/24"]
}

variable "eks_cluster_name" {
  description = "Name for the EKS cluster."
  type        = string
  default     = "dinosaur-eks-cluster"
}

variable "node_group_instance_types" {
  description = "EC2 instance types for EKS node group."
  type        = list(string)
  default     = ["t3.medium"]
}

variable "jump_game_bucket_name" {
  description = "S3 bucket name for Jump Game static site."
  type        = string
  default     = "jump-game-static-site-unique"
}

output "eks_cluster_name" {
  value = module.eks.cluster_name
}

output "eks_cluster_endpoint" {
  value = module.eks.cluster_endpoint
}

output "eks_cluster_security_group_id" {
  value = module.eks.cluster_security_group_id
}

output "s3_static_site_url" {
  value = aws_s3_bucket_website_configuration.jump_game.website_endpoint
}

output "cloudfront_distribution_domain_name" {
  value = aws_cloudfront_distribution.jump_game_cdn.domain_name
}

module "vpc" {
  source  = "terraform-aws-modules/vpc/aws"
  version = "~> 5.0"

  name = "dinosaur-vpc"
  cidr = var.vpc_cidr

  azs             = ["${var.aws_region}a", "${var.aws_region}b"]
  public_subnets  = var.public_subnet_cidrs
  private_subnets = var.private_subnet_cidrs

  enable_nat_gateway   = true
  single_nat_gateway   = true
  enable_dns_hostnames = true
  enable_dns_support   = true

  tags = {
    Terraform   = "true"
    Environment = "dev"
  }
}

module "eks" {
  source          = "terraform-aws-modules/eks/aws"
  version         = "~> 20.0"
  cluster_name    = var.eks_cluster_name
  cluster_version = "1.29"
  vpc_id          = module.vpc.vpc_id
  subnet_ids      = module.vpc.private_subnets

  eks_managed_node_group_defaults = {
    instance_types = var.node_group_instance_types
  }

  eks_managed_node_groups = {
    default = {
      min_size     = 1
      max_size     = 3
      desired_size = 2
    }
  }

  tags = {
    Terraform   = "true"
    Environment = "dev"
  }
}

data "aws_eks_cluster" "cluster" {
  name = module.eks.cluster_name
}

data "aws_eks_cluster_auth" "cluster" {
  name = module.eks.cluster_name
}

resource "aws_s3_bucket" "jump_game" {
  bucket = var.jump_game_bucket_name
  force_destroy = true

  tags = {
    Name        = "Jump Game Static Site"
    Environment = "dev"
  }
}

resource "aws_s3_bucket_public_access_block" "jump_game" {
  bucket = aws_s3_bucket.jump_game.id

  block_public_acls   = false
  block_public_policy = false
  ignore_public_acls  = false
  restrict_public_buckets = false
}

resource "aws_s3_bucket_policy" "jump_game" {
  bucket = aws_s3_bucket.jump_game.id
  policy = data.aws_iam_policy_document.jump_game.json
}

data "aws_iam_policy_document" "jump_game" {
  statement {
    actions   = ["s3:GetObject"]
    resources = ["${aws_s3_bucket.jump_game.arn}/*"]
    principals {
      type        = "*"
      identifiers = ["*"]
    }
    effect = "Allow"
  }
}

resource "aws_s3_bucket_website_configuration" "jump_game" {
  bucket = aws_s3_bucket.jump_game.id
  index_document {
    suffix = "index.html"
  }
  error_document {
    key = "error.html"
  }
}

resource "aws_cloudfront_distribution" "jump_game_cdn" {
  origin {
    domain_name = aws_s3_bucket.jump_game.bucket_regional_domain_name
    origin_id   = "jump-game-s3-origin"
    s3_origin_config {
      origin_access_identity = ""
    }
  }

  enabled             = true
  is_ipv6_enabled     = true
  default_root_object = "index.html"

  default_cache_behavior {
    allowed_methods  = ["GET", "HEAD"]
    cached_methods   = ["GET", "HEAD"]
    target_origin_id = "jump-game-s3-origin"

    forwarded_values {
      query_string = false
      cookies {
        forward = "none"
      }
    }
    viewer_protocol_policy = "redirect-to-https"
  }

  price_class = "PriceClass_100"

  restrictions {
    geo_restriction {
      restriction_type = "none"
    }
  }

  viewer_certificate {
    cloudfront_default_certificate = true
  }

  tags = {
    Environment = "dev"
  }
}

resource "kubernetes_deployment" "dinosaur" {
  metadata {
    name      = "dinosaur-deployment"
    namespace = "default"
    labels = {
      app = "dinosaur"
    }
  }
  spec {
    replicas = 2
    selector {
      match_labels = {
        app = "dinosaur"
      }
    }
    template {
      metadata {
        labels = {
          app = "dinosaur"
        }
      }
      spec {
        container {
          name  = "nginx"
          image = "nginx:latest"
          port {
            container_port = 80
          }
        }
      }
    }
  }
}

resource "kubernetes_service" "dinosaur" {
  metadata {
    name      = "dinosaur-service"
    namespace = "default"
    labels = {
      app = "dinosaur"
    }
    annotations = {
      "service.beta.kubernetes.io/aws-load-balancer-type" = "alb"
    }
  }
  spec {
    selector = {
      app = "dinosaur"
    }
    port {
      port        = 80
      target_port = 80
    }
    type = "LoadBalancer"
  }
}
