plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "FastAPI Web Application" as fastapi_app <<Python, FastAPI>> {
}

node "Uvicorn ASGI Server" as uvicorn <<Python, Uvicorn>> {
}

node "HashiCorp Vault" as vault <<Secrets Management>> {
}

cloud "Terraform-managed Infrastructure" as terraform <<IaC>> {
}

actor "User" as user

user --> fastapi_app : HTTPS Requests
fastapi_app --> uvicorn : ASGI Protocol
fastapi_app --> vault : Secrets, Encryption, Transformation
fastapi_app --> terraform : Reads Infra State (optional)

@enduml
