plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Developer Workstation" as dev {
  artifact "DevContainer" as devcontainer
  artifact "CLI Tools (awscli, aws-vault, terraform)" as clitools
}

node "Docker Container" as docker {
  artifact "Go Application\n(cmd/awsS3BucketList)" as goapp
}

cloud "AWS Cloud" as aws {
  node "AWS S3" as s3
  node "AWS Credentials/Config" as awscreds
}

devcontainer -down-> docker : builds & runs
clitools -down-> docker : used for setup

docker -down-> goapp : executes

goapp -right-> s3 : uses AWS SDK

goapp -right-> awscreds : uses credentials/config

@enduml
