plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "Developer Workstation" as dev {
  artifact "Source Code" as src
}

node "Docker Container\n(ubuntu:latest)" as docker {
  component "Terraform CLI\n1.7.4" as terraform
  component "AWS CLI" as awscli
  component "Infrastructure Tooling\n(wget, unzip, curl, openssh-client, git, iputils-ping)" as tools
}

cloud "AWS Cloud" as aws {
  artifact "AWS Resources\n(Provisioned by Terraform)" as aws_resources
}

dev -down-> docker : Builds & Runs
src -down-> docker : Mounted/Used

docker -down-> terraform : Executes
terraform -down-> awscli : Uses for AWS API
awscli -down-> aws_resources : Provisions/Manages

@enduml
