plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "User" as user

node "AWS EKS Cluster" as eks {
  node "Kubernetes Deployment\nloan-calc" as k8s_deploy
  node "Kubernetes Service\nloan-calc-service (LoadBalancer)" as k8s_svc
}

cloud "AWS Application Load Balancer" as alb

node "Docker Container\nnginx:alpine" as nginx {
  artifact "Web Application\n(Loan Calculator)\nHTML/CSS" as webapp
}

user --> alb : HTTP/HTTPS
alb --> k8s_svc : Forwards traffic
k8s_svc --> k8s_deploy : Routes to pods
k8s_deploy --> nginx : Runs container
nginx --> webapp : Serves static files

@enduml
