plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "User" as user

node "AWS EKS Cluster" as eks {
  node "Kubernetes Deployment" as k8s_deploy {
    node "Nginx Static Web Server\n(HTML/CSS/JS)" as nginx
  }
  node "Kubernetes Service\n(AWS Load Balancer)" as k8s_svc
}

cloud "Docker Image Registry\n(GitHub Container Registry)" as registry

cloud "Terraform (EKS Provisioning)" as terraform

user --> k8s_svc : HTTP/HTTPS
k8s_svc --> nginx : Forwards traffic
nginx --> registry : Pulls Docker image
terraform --> eks : Provisions EKS

@enduml
