plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

node "User" as user
node "Web Browser" as browser

node "AWS Cloud" {
  node "Kubernetes Cluster" {
    node "K8s Deployment: magic-color\n(Docker, Nginx, HTML/CSS/JS)" as k8s_deploy
    node "K8s Service: LoadBalancer (ALB)" as k8s_svc
  }
}

node "CI/CD Pipeline" {
  node "GitHub Actions" as github_actions
  node "Jenkins" as jenkins
}

user --> browser : HTTP
browser --> k8s_svc : HTTP (via ALB)
k8s_svc --> k8s_deploy : HTTP

github_actions --> k8s_deploy : Deploy
jenkins --> k8s_deploy : Deploy

@enduml
